BulkCreateRecords.FieldValuesFormula:
!
LET $columnMap :=
INDEXES_TO_KEYS(
  EACH [Import to Column],
  EACH [Import Key]
);

LET $targetFormKey := HASH_GET($columnMap, "id");

LET $mappings := 
ARRAY_WHERE(ARRAY_TRANSPOSE(ARRAY(
  EACH [Import Key],
  EACH [Import to Column]
)), $(i, item) ARRAY_INDEX($item, 2) <> NULL); 

LET $categoryRecordData := 
ARRAY_DISTINCT(
  ARRAY_SELECT(
    [Supplier Subcategories], $(i, item)

    LET $categoryData := HASH_GET($item, "parentCategory");

    LET $content := 
    INDEXES_TO_KEYS(
      ARRAY_SELECT(
        ARRAY_FIRST(ARRAY_TRANSPOSE($mappings)), $(k, key) 
        HASH_GET($categoryData, $key)),
      ARRAY_LAST(ARRAY_TRANSPOSE($mappings))
    ); 

    RETURN 
    HASH(
      "content", $content,
      "parent", NULL,
      "match", HASH($targetFormKey, HASH_GET($categoryData, "id"))
    )
  )
);

RETURN 
$categoryRecordData

