LET $targetDataKey := "supplierLists";

LET $mappings := 
INDEXES_TO_KEYS(
  EACH [Import to Column],
  EACH [Import Key]
);

LET $targetFormKey := HASH_GET($mappings, "id");

LET $lookupColumns := ARRAY("RecordID", $targetFormKey);

LET $existingData := 
DB_LOOKUPALL(
  $lookupColumns, 
  [Target Form]
);

LET $existingDataHashed := 
INDEXES_TO_KEYS(
  ARRAY_INDEX(ARRAY_TRANSPOSE($existingData), 1),
  ARRAY_INDEX(ARRAY_TRANSPOSE($existingData), 2)
);

LET $mappings := 
ARRAY_WHERE(ARRAY_TRANSPOSE(ARRAY(
  EACH [Import Key],
  EACH [Import to Column]
)), $(i, item) ARRAY_INDEX($item, 2) <> NULL); 

LET $dataToCreateOrUpdate := 
ARRAY_DISTINCT(
  ARRAY_SELECT(
    [Supplier Subcategories], $(i, item)

    LET $data := 
    IF( 
      $targetDataKey = "supplierLists", 
        ARRAY_FIRST(HASH_GET($item, $targetDataKey)),
      HASH_GET($item, $targetDataKey)
    );
    //----------------------
    LET $allCategoryMatches := 
    ARRAY_WHERE(
      [Supplier Subcategories], $(j, jitem) 
      HASH_GET(ARRAY_FIRST(HASH_GET($jitem, "supplierLists")), "id") = HASH_GET($data, "id")
    );

    LET $allCategoryMappings := 
    ARRAY_DISTINCT(
      ARRAY_SELECT(
        $allCategoryMatches, $(i, item) 
        ARRAY(
          HASH_GET(HASH_GET($item, "parentCategory"), "id"),
          HASH_GET(HASH_GET($item, "parentCategory"), "name"),
          HASH_GET(HASH_GET($item, "subCategory"), "id"),
          HASH_GET(HASH_GET($item, "subCategory"), "name")
        )
      )
    );

    LET $allCategoryMappingsHashed := 
    INDEXES_TO_KEYS(
      ARRAY_TRANSPOSE($allCategoryMappings),
      ARRAY(
        [Import Column - Category ID],      
        [Import Column - Category Name],    
        [Import Column - Subcategory ID],   
        [Import Column - Subcategory Name]    
      )
    );

    //----------------------

    LET $content := 
    HASH_MERGE(
      INDEXES_TO_KEYS(
        ARRAY_SELECT(
          ARRAY_FIRST(ARRAY_TRANSPOSE($mappings)), $(k, key) 
          HASH_GET($data, $key)),
        ARRAY_LAST(ARRAY_TRANSPOSE($mappings))
      ),
      $allCategoryMappingsHashed
    ); 

    LET $id := HASH_GET($data, "id");
    LET $match := HASH_GET($existingDataHashed, $id); 

    LET $result := 
    HASH_MERGE(
      HASH( 
        "content", $content
	    ),
      IF(
		    $match <> NULL, 
		    HASH("match", HASH($targetFormKey, HASH_GET($data, "id")))
	    )
    );

    RETURN 
    $result
  )
);

RETURN $dataToCreateOrUpdate

