TriggerCustomEvent.EventArgumentsFormula:
LET $startDate:= DATEADD("day", 1, TODAY());
LET $endDate:= DATEADD("day", 7, $startDate);
LET $decisionDate:=  DATEADD("day", 7, $endDate);
LET $rfxNamePrefix:= "Dipika - Nx ";

LET $newOpportunityModel := 
   HASH(
	  //this is really a readonly property, but to create a new one I need to set this none empty
	  "refNumber", "VPXXXXXXXX",	 

	  // these can be empty to begin with, but I've set them here for the purposes of testing
	  "name", 			$rfxNamePrefix &"- Test RFx created by Nx instance on "& T(DATEFORMAT(NOW(), "f")),
	  "description",	$rfxNamePrefix &"- Description of test RFx created by Nx client on "& DATEFORMAT(NOW(), "f"),
	  "budgetValue",     500,

	 //how the suppliers are going to be evaluated
      "responseSelectionType", "Quick Select",

      // an RFx needs a nominated start date AND end date (UTC) in "Big O" format (eg. 2022-11-15T21:46:53.6853038Z)
	 //jsonencode changes datetime to utc
      "startDateUtc",     $startDate,
      // assume we're minting a regular RFx that requires a life time of AT LEAST 7 days.
      "endDateUtc",       $endDate,
      // assume we're minting a regular RFx that defaults to a 
      "decisionDateUtc",  $decisionDate,

      // RFx is NOT complete
      "isComplete", FALSE,

      // RFx is NOT "same day close"
      "sameDayCloseEnabled", FALSE,
      "isSameDayClose", FALSE,

      // RFx is NOT a "demo rfx"
      "isDemo", FALSE,

     // Does the RFx require a quote to be provided, by default this is true
      "quotedAmountRequired", TRUE,

     // By default the value of the RFx is NOT disclosed to the supplier(s)
      "valueDisclosed", FALSE,

      // use default value range index
      "valueRangeId", 11,

      // create an RFx that is at "step 2" in RFx Wizard
      "wizardStep", "1"
  );

RETURN HASH("opportunityModel", $newOpportunityModel)

