) ));
RETURN
ARRAY_SELECT( $suppliers, $(i,item) HASH( "value", HASH_GET($item, "id"), "text", HASH_GET($item, "id")&" - "&HASH_GET($item, "businessName")& " ("& HASH_GET($item, "businessNumber") & ")" )
)

