FieldEntry.Formula:
LET $new:= HASH_GET([Temp], "values");
LET $existing:= [Responses];

RETURN
ARRAY_WHERE(
	ARRAY_CONCAT($new, $existing), $(i,item)
  	$item <> NULL
)

LET $response:=
IF(
  [Response Code] = "200", 
	HASH_GET(JSONDECODE([Response Body]), "values"),
  NULL
);

RETURN
IF(
  HASH_GET($eventargs, "next")= NULL, $response, ARRAY_CONCAT([Temp], $response)
  )

