FieldEntry.Formula:
//$eventargs structure
//Opportunity ID, number
//Response ID, number (Optional)
//DocumentID, number
//Request URL, optional, prioritised if provided

$eventargs

LET $opportunityID := HASH_GET([Request Params], "Opportunity ID");
LET $responseID := HASH_GET([Request Params], "Response ID");
LET $documentID := HASH_GET([Request Params], "Document ID");
LET $requestURL := HASH_GET([Request Params], "Request URL");
LET $GTMBaseURL := HASH_GET([Plugin Result], "ApiUrl") & "api/v1/";

RETURN

IF( 
  //URL Fed in directly
  $requestURL <> NULL, 
    $requestURL, 

  //Response document
  $responseID <> NULL, 
    $GTMBaseURL & 
      "Opportunities/" & URLENCODE($opportunityID) & 
      "/Responses/" & URLENCODE($responseID) & 
      "/Documents/" & URLENCODE($documentID),

  //Opportunity document
  $GTMBaseURL & 
    "Opportunities/" & URLENCODE($opportunityID) & 
    "/Documents/" & URLENCODE($documentID)
)

