FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $createRecords:= HASH_GET($eventargs, "Create Records"); //potential values - ("Category", "Subcategory", "Supplier List", "Suppliers", "Supplier List Children", "Responses")
LET $targetForm:= IF( $createRecords = "Category", "GTM Category", $createRecords = "Subcategory", "GTM Subcategory", $createRecords = "Supplier List", "Supplier List", $createRecords = "Supplier", "Supplier", $createRecords = "Supplier List Child", "Supplier List Member"
// , $createRecords = "Response", );
LET $columnHash :=
IF( $createRecords = "Category", HASH( "id", "ID", "name", "Name", "links", "Links" ),
$createRecords = "Subcategory", HASH( "id", "ID", "name", "Name", "links", "Links" ),
$createRecords = "Supplier List", HASH( "id", "Supplier List ID", "name", "Supplier List Name" ),
$createRecords = "Supplier", HASH( "id", "Supplier ID", "userId", "User ID", "profileId", "Profile ID", "vendorGroupPanelLinkId", "Supplier Group Supplier List Link ID", "businessName", "Supplier Name", "businessNumber", "Business Number", "fullName", "Full Name", "email", "Email" ), $createRecords = "Supplier List Child", HASH( "userId", "User ID", "supplierListId", "Supplier List ID" )
// , $createRecords = "Response", );
//additional stuff to append to basic hash LET $append:= IF( $createRecords = "Subcategory", HASH( "Target Parent Form", ARRAY("GTM Category"), "Parent Form ID Column", ARRAY("ID") ),
$createRecords = "Supplier List", HASH( "Import Column - Category ID", ARRAY("Category ID"), "Import Column - Category Name", ARRAY("Category Name"), "Import Column - Subcategory ID", ARRAY("Subcategory ID"), "Import Column - Subcategory Name", ARRAY("Subcategory Name") )
);
RETURN HASH_MERGE( HASH( "Target Form", $targetForm, "Import to Column", ARRAY_SELECT( EACH [Import Key], $(i,item) HASH_GET($columnHash, $item) )
), $append )

BulkCreateRecords.FieldValuesFormula:
!
LET $columnMap :=
INDEXES_TO_KEYS(
  EACH [Import to Column],
  EACH [Import Key]
);

LET $targetFormKey := HASH_GET($columnMap, "id");

LET $mappings := 
ARRAY_WHERE(ARRAY_TRANSPOSE(ARRAY(
  EACH [Import Key],
  EACH [Import to Column]
)), $(i, item) ARRAY_INDEX($item, 2) <> NULL); 

LET $categoryRecordData := 
ARRAY_DISTINCT(
  ARRAY_SELECT(
    [Supplier Subcategories], $(i, item)

    LET $categoryData := HASH_GET($item, "parentCategory");

    LET $content := 
    INDEXES_TO_KEYS(
      ARRAY_SELECT(
        ARRAY_FIRST(ARRAY_TRANSPOSE($mappings)), $(k, key) 
        HASH_GET($categoryData, $key)),
      ARRAY_LAST(ARRAY_TRANSPOSE($mappings))
    ); 

    RETURN 
    HASH(
      "content", $content,
      "parent", NULL,
      "match", HASH($targetFormKey, HASH_GET($categoryData, "id"))
    )
  )
);

RETURN 
$categoryRecordData

!
LET $columnMap := 
INDEXES_TO_KEYS(
  EACH [Import to Column],
  EACH [Import Key]
);

LET $targetFormKey := HASH_GET($columnMap, "id");

LET $mappings := 
ARRAY_WHERE(ARRAY_TRANSPOSE(ARRAY(
  EACH [Import Key],
  EACH [Import to Column]
)), $(i, item) ARRAY_INDEX($item, 2) <> NULL);

LET $parentEFormType := [Target Parent Form];
LET $parentIDColumn := [Parent Form ID Column];

LET $categoryRecordData := 
ARRAY_DISTINCT(
  ARRAY_SELECT(
    [Supplier Subcategories], $(i, item)

    LET $parent := HASH_GET($item, "parentCategory");
    LET $parentRecordID := 
    DB_LOOKUP(
      "RecordID", 
      $parentEFormType, 
      HASH($parentIDColumn, HASH_GET($parent, "id"))
    );

    LET $categoryData := HASH_GET($item, "subCategory");

    LET $content := 
      INDEXES_TO_KEYS(
        ARRAY_SELECT(
          ARRAY_FIRST(ARRAY_TRANSPOSE($mappings)), $(k, key) 
          HASH_GET($categoryData, $key)),
        ARRAY_LAST(ARRAY_TRANSPOSE($mappings))
      ); 

    LET $result :=  
    HASH( 
      "content", $content,
		  "parent", ARRAY($parentEFormType, $parentRecordID),
      "match", HASH($targetFormKey, HASH_GET($categoryData, "id"))
    );

     RETURN $result
  )
);

RETURN 
$categoryRecordData

!

LET $targetDataKey := "supplierLists";

LET $mappings := 
INDEXES_TO_KEYS(
  EACH [Import to Column],
  EACH [Import Key]
);

LET $targetFormKey := HASH_GET($mappings, "id");

LET $lookupColumns := ARRAY("RecordID", $targetFormKey);

LET $existingData := 
DB_LOOKUPALL(
  $lookupColumns, 
  [Target Form]
);

LET $existingDataHashed := 
INDEXES_TO_KEYS(
  ARRAY_INDEX(ARRAY_TRANSPOSE($existingData), 1),
  ARRAY_INDEX(ARRAY_TRANSPOSE($existingData), 2)
);

LET $mappings := 
ARRAY_WHERE(ARRAY_TRANSPOSE(ARRAY(
  EACH [Import Key],
  EACH [Import to Column]
)), $(i, item) ARRAY_INDEX($item, 2) <> NULL); 

LET $defaultContent := 
HASH(
  "Category Reporting Enabled", FALSE,
  "Reporting Type", "Sales Totals", 
  "Reporting Abstraction", "Individual Transactions", 
  "Fee Calculation", "Percentage of Total Sales", 
  "Fee - Percentage of Total Sales", 2
); 

LET $dataToCreateOrUpdate := 
ARRAY_DISTINCT(
  ARRAY_SELECT(
    [Supplier Subcategories], $(i, item)

    LET $data := 
    IF( 
      $targetDataKey = "supplierLists", 
        ARRAY_FIRST(HASH_GET($item, $targetDataKey)),
      HASH_GET($item, $targetDataKey)
    );
    //----------------------
    LET $allCategoryMatches := 
    ARRAY_WHERE(
      [Supplier Subcategories], $(j, jitem) 
      HASH_GET(ARRAY_FIRST(HASH_GET($jitem, "supplierLists")), "id") = HASH_GET($data, "id")
    );

    LET $allCategoryMappings := 
    ARRAY_DISTINCT(
      ARRAY_SELECT(
        $allCategoryMatches, $(i, item) 
        ARRAY(
          HASH_GET(HASH_GET($item, "parentCategory"), "id"),
          HASH_GET(HASH_GET($item, "parentCategory"), "name"),
          HASH_GET(HASH_GET($item, "subCategory"), "id"),
          HASH_GET(HASH_GET($item, "subCategory"), "name")
        )
      )
    );

    LET $allCategoryMappingsHashed := 
    INDEXES_TO_KEYS(
      ARRAY_TRANSPOSE($allCategoryMappings),
      ARRAY(
        [Import Column - Category ID],      
        [Import Column - Category Name],    
        [Import Column - Subcategory ID],   
        [Import Column - Subcategory Name]    
      )
    );

    //----------------------

    LET $content := 
    HASH_MERGE(
      INDEXES_TO_KEYS(
        ARRAY_SELECT(
          ARRAY_FIRST(ARRAY_TRANSPOSE($mappings)), $(k, key) 
          HASH_GET($data, $key)),
        ARRAY_LAST(ARRAY_TRANSPOSE($mappings))
      ),
      $allCategoryMappingsHashed
    ); 

    LET $id := HASH_GET($data, "id");
    LET $match := HASH_GET($existingDataHashed, $id); 

    LET $result := 
    HASH_MERGE(
      HASH( 
        "content", 
		IF(
		  $match = NULL, 
		  HASH_MERGE(
			$defaultContent, 
			$content
		  )
	    ),

		"match", HASH($targetFormKey, HASH_GET($data, "id")))
    );

    RETURN 
    $result
  )
);

RETURN $dataToCreateOrUpdate

!

LET $targetDataKey := [Suppliers];

LET $mappings := 
INDEXES_TO_KEYS(
  EACH [Import to Column],
  EACH [Import Key]
);

LET $key := "userId";

LET $targetFormKey := HASH_GET($mappings, $key);

LET $lookupColumns := ARRAY("RecordID", $targetFormKey);

LET $keys := ARRAY_SELECT([Suppliers], $(i, item) HASH_GET($item, $key)); 

LET $distinctSuppliers := 
ARRAY_DISTINCT(ARRAY_SELECT(
  $keys, $(k, thisKey) 
  ARRAY_FIRST(ARRAY_WHERE(
    [Suppliers], $(i, item) 
    HASH_GET($item, $key) = $thisKey
  ))
)); 

LET $mappings := 
ARRAY_WHERE(ARRAY_TRANSPOSE(ARRAY(
  EACH [Import Key],
  EACH [Import to Column]
)), $(i, item) ARRAY_INDEX($item, 2) <> NULL); 

LET $dataToCreateOrUpdate := 
ARRAY_DISTINCT(
  ARRAY_SELECT(
    $distinctSuppliers, $(i, data)

    LET $content := 
    HASH_MERGE(
      INDEXES_TO_KEYS(
        ARRAY_SELECT(
          ARRAY_FIRST(ARRAY_TRANSPOSE($mappings)), $(k, key) 
          HASH_GET($data, $key)),
        ARRAY_LAST(ARRAY_TRANSPOSE($mappings))
      )
    ); 

    LET $result := 
    HASH_MERGE(
      HASH("content", $content),
		  HASH("match", HASH($targetFormKey, HASH_GET($data, $key)))
	  );

    RETURN 
    $result
  )
);

RETURN 
$dataToCreateOrUpdate

