LET $data := ARRAY_SELECT( [Responses], $(r, response) HASH( "EventArgs", $response, "Name", HASH_GET($response, "name"), "Ref Number", HASH_GET($response, "refNumber"), "Evaluation Opportunity Id",HASH_GET($response, "opportunityId"), "Evaluation Response Id", HASH_GET($response, "id") ) );
RETURN ARRAY_SELECT( $data, $(i, item) HASH( "content", $item, "match", HASH( "Ref Number", HASH_GET($item, "Ref Number") ), "parent", ARRAY("VP Opportunity", $parentRecordID) ) )
