  ISBLANK([Action]), 
  [Type] = NULL//, 
  //[Templated Actions Exist] = TRUE
)

FieldEntry.Formula:
LET $previous := EACH [Attendee];
LET $invited := [Meeting Invitees - Users];

LET $notListed := 
ARRAY_WHERE(
  	$invited, $(i, item) 
    ARRAY_INDEXOF(
	  $previous, 
	  EBMS_GET_ROLE_DISPLAYNAME($item)
  ) = 
  NULL()
);

LET $displayNames := 
ARRAY_SELECT(
  	$notListed, $(i, item) 
  EBMS_GET_ROLE_DISPLAYNAME($item) 
); 

RETURN 
ARRAY_CONCAT(
  $previous, 
  $displayNames
)

