Settings.LandingViewFormula:
LET $user := EBMS_GET_CURRENT_USER();
LET $roles := EBMS_GET_ANCESTOR_ROLES($user);
LET $type := [Type];

LET $userIsInEveryone := ARRAY_INDEXOF($roles, "Everyone") > 0;
//LET $userIsInAdministrators := ARRAY_INDEXOF($roles, "Administrators") > 0;
LET $userIsInSystemBuilders := ARRAY_INDEXOF($roles, "System Builders") > 0;

LET $defaultview := IF(
  	$userIsInSystemBuilders = TRUE,  COALESCE($requestedView,  "Default"),
  	"Default" 
  );

RETURN 
IF(

  	AND( 
	  $userIsInSystemBuilders = TRUE,
	   $requestedView <> NULL,
	  OR(
		AND($type = "Template", $requestedView <> "Default"),   //for template type, default view should not be visible even for sys builders
		AND($type <> "Template", $requestedView <> "Template")   //for non-template type, template view should not be visible even for sys builders
		)
  	),
  	COALESCE($requestedView,  "Default"),

  	$type = "Template", "Template", // for admin and everyone

  	"Default"
  )
