Column.DefaultValueFormula:
//Get modules that the user has access to
LET $currentUserRoles := 
(
  LET $currentUser := EBMS_GET_CURRENT_USER(); 
  LET $userRoles := EBMS_GET_ANCESTOR_ROLES($currentUser);
  RETURN $userRoles
); 

LET $columns := 
ARRAY(
  "Module Name", 
  "Module Settings EForm",
  "Module Access",
  "Module Home Page"
);

LET $moduleData :=
DB_LOOKUPALL(
  $columns,
  "Global Settings", 
  "[RecordID] FAST_EQUAL_TO 1"
);

LET $result :=  
ARRAY_WHERE(
  $moduleData, $(i, item) 
  LET $rolesWithAccess := ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Module Access"));
  RETURN 
  ARRAY_LENGTH(
    ARRAY_WHERE(
	  $rolesWithAccess, $(r, role)
	  ARRAY_INDEXOF($currentUserRoles, $role) > 0
    )
  ) > 0
);

LET $approvedList := 
ARRAY(
  //"Policy Guide", 
  "Procurement Planning",
  //"Go To Market", 
  "Advanced Evaluations",
  "Advanced Contracts",
  "Purchase Orders", 
  "Vendors", 
  "Information Requests", 
  "Projects", 
  "Risks", 
  "Approvals"//,
  //"Analytics"
); 

LET $modulesUserHasAccessTo :=  
ARRAY_WHERE(
  ARRAY_SELECT($result, $(i, item) 
    HASH(
	  "Module Name", ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Module Name")),
      "Module Settings EForm", ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Module Settings EForm")),
	  "Module Home Page", ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Module Home Page"))
	)
  ), $(i, item) 
  $item <> NULL
);

LET $moduleList := 
ARRAY_WHERE(
  $modulesUserHasAccessTo, $(i, item) 
  ARRAY_INDEXOF($approvedList, HASH_GET($item, "Module Name")) > 0
);

//Get the reports for each module
LET $reportColumns := 
ARRAY(    
  "Report Name", 
  "Report EFormType", 
  "Report Profile Name", 
  "Report Type",
  "Report Enabled",
  "Report Visibility"
);

//Custom function
LET $getReports := $(EFormName)
(
  LET $data := 
  DB_LOOKUPALL(
  	$reportColumns, 
  	$EFormName, 
	"[Report Name] IS NOT NULL"
  ); 

  LET $hashed := 
  ARRAY_SELECT(
	$data, $(i, item) 
	INDEXES_TO_KEYS($item, $reportColumns)
  ); 

  LET $urls := 
  ARRAY_SELECT(
	$hashed, $(i, item) 
	LET $eformName := HASH_GET($item, "Report EFormType");
	LET $profile := HASH_GET($item, "Report Profile Name");
	LET $reportType := HASH_GET($item, "Report Type");
	LET $reportName := HASH_GET($item, "Report Name");
	RETURN 
	  HASH(
		"value", 
	    IF( 
	      $reportType = "Link and Export", 
	      HASH(
			"navigation", "tabularreport/" & URLENCODE($eformName) & "?profile=" & URLENCODE($profile),
			"export","ExportTabularReport.ashx?EFormType=" & URLENCODE($eformName) & "&profile=" & URLENCODE($profile) & "&format=xls"
		  ), 

	      $reportType = "EForm", 
	      HASH("navigation", "records/" & URLENCODE($eformName) & "/new?view=Default")
	    ), 
	  "text", $reportName
	)
  );

  LET $filteredURLs := ARRAY_WHERE($urls, $(i, item) HASH_GET($item, "value") <> NULL); 

  RETURN IF(ARRAY_LENGTH($data) > 0, $urls)  
); 

//Hard coded reports
