LET $dynamicResults := 
ARRAY_SELECT(
  $moduleList, $(m, module) 
  LET $moduleSettingsForm := HASH_GET($module, "Module Settings EForm");
  LET $moduleName := HASH_GET($module, "Module Name");
  LET $moduleHomePage := HASH_GET($module, "Module Home Page");
  RETURN
  HASH(
    "url", "records/" & URLENCODE($moduleHomePage) & "/new?view=Default&$Navigation=Reports",
    "reports", CALL($getReports, $moduleSettingsForm)
  )
);


LET $result :=  
HASH_MERGE(
  INDEXES_TO_KEYS(
	$dynamicResults, 
	ARRAY_SELECT(
	  $moduleList, $(i, item) 
	  HASH_GET($item, "Module Name")
	)
  ),
  $staticResults
);

RETURN $result

LET $currentUser := EBMS_GET_CURRENT_USER; 

LET $currentRoles := EBMS_GET_ANCESTOR_ROLES($currentUser);

LET $userMapping := 
INDEXES_TO_KEYS(
  ARRAY_SELECT($currentRoles, $(i, item) TRUE),
  $currentRoles
);
RETURN $userMapping

Column.Formula:
LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[RecordID] > 0, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"

				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

Column.ValidationFormula:
IF(
  AND(
	ISBLANK($thisValue),
	OR(
		[Report Type] = "Link and Export",
		[Report Type] = "Chart"
	 )
	), $thisColumn&" is requied",
  TRUE
 )

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $deconstruct := $(url)(
LET $reportUrl := TRIM($url);
LET $reportType := REGEX_MATCH($reportUrl, "(?i)(tabularreport|chartingreport|records)");
LET $reportTypeLower := LOWER($reportType);
RETURN
IF(
  $reportType = NULL, 
  HASH(
    "Show Detail", 				ARRAY(FALSE),
    "Report Name",				ARRAY(NULL),
    "Report Visibility",		ARRAY(NULL),
    "Report Type", 				ARRAY(NULL),
    "Report EFormType",			ARRAY(NULL),
    "Report Profile Name",      ARRAY(NULL)
  ),

  LET $reportTypeName := 
    IF(
  	  $reportTypeLower = "tabularreport", 	"Link and Export", 
  	  $reportTypeLower = "chartingreport", 	"Chart",
  	  $reportTypeLower = "records", 			  "EForm"
    );

  LET $eformType := REGEX_MATCH($reportUrl, $reportType& "/(.*?(?=\?|\/|$))" );

  LET $profileRequired := OR( $reportTypeLower = "tabularreport", $reportType = "chartingreport");	

  LET $profile  := 
  IF( 
  	$profileRequired = TRUE,
  	COALESCE( REGEX_MATCH($reportUrl, "profile=(.*?(?=\&|$))" ), "Summary")
  );

  RETURN
    HASH(
      "Navigation", 			ARRAY("Confirm"), 
	  "Show Detail",			ARRAY(TRUE),
      "Report Name",			ARRAY(URLDECODE(COALESCE($profile, $eformType))),
      "Report Visibility",		ARRAY(ARRAY(ROLE("Everyone"))),
      "Report Type", 			ARRAY($reportTypeName),
      "Report EFormType",		ARRAY(URLDECODE($eformType)),
      "Report Profile Name",    ARRAY(URLDECODE($profile))
    )	
  )
);

RETURN CALL($deconstruct, [Report Url])

LET $cols:=
ARRAY(
  	"Report Type",				
  	"Report EFormType",			
  	"Report Profile Name",		
  	"Report Name",
  	"Report Visibility",
  	"Report Enabled"
  );

LET $targetData := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH target.[Report Type], 			//1
		EACH target.[Report EFormType], 
		EACH target.[Report Profile Name], 
		EACH target.[Report Name],			//4
		EACH target.[Report Visibility],
	  	EACH target.[Report Enabled]
	)
);

LET $cleanData:=
ARRAY_WHERE(
  	$targetData, $(i,item)
  	AND(
	  ARRAY_INDEX($item, 1) <> NULL,
	  ARRAY_INDEX($item, 4) <> NULL
	  )
);

LET $data:= ARRAY_TRANSPOSE($cleanData);

LET $new:= 
ARRAY(
  	[Report Type],
  	[Report EFormType],
  	[Report Profile Name],
  	[Report Name],
  	[Report Visibility],
  	TRUE
  );

LET $build:= $(i)(
  	ARRAY_CONCAT(ARRAY_INDEX($data, $i), ARRAY_INDEX($new, $i) ) 
);


RETURN
	ARRAY_TO_HASH(
	  $cols, $(i,item) $item,
	  $(i,item)  CALL($build, $i)
	  )
