FieldEntry.Formula:
LET $submissiontype :=
IF(
	OR( 
		ISBLANK([Type]),
		DB_LOOKUP(
			"RecordID", 
			"Approval Workflow", 
			HASH("Submission Type", [Type], "LineNumber", 1)
		) = NULL()
	),	
  	"[Submission Type] = 'Master List - " & [Module] & "'", //fallback
	"[Submission Type]=" & DB_ENCODECONSTANT([Type])
);


LET $filters := 
ARRAY(
    "([Upper Price] IS NULL OR [Upper Price]>" & DB_ENCODECONSTANT(COALESCE([Submission Value], [Estimated Contract Value], 0)) & ")",
    "([Lower Price] IS NULL OR [Lower Price]<=" & DB_ENCODECONSTANT(COALESCE([Submission Value], [Estimated Contract Value], 0)) & ")",
    $submissiontype
);

LET $filterstring := ARRAY_JOIN(ARRAY_WHERE($filters, $(i, item) $item != NULL()), " AND ");

LET $lookup := 
DB_LOOKUPALL(
	  ARRAY(
	  "Source EFormType", 
	  "Source RecordID",
	  "Who?",
	  "Approval Type"
    ), 
    "Approval Workflow", 
    $filterstring
);

LET $matches := 
ARRAY_SELECTMANY(
	[Parent Chain], $(i, item)
	ARRAY_WHERE(
		$lookup, $(j, jitem) 
		AND(
			ARRAY_INDEX($item, 1) = ARRAY_INDEX($jitem, 1),
			ARRAY_INDEX($item, 2) = ARRAY_INDEX($jitem, 2)
		)
	)
);

LET $customRoute := ARRAY_WHERE($matches, $(i, item) $item <> ARRAY());

RETURN 
IF( 
	$customRoute <> ARRAY(), 
	ARRAY_SELECT($customRoute, $(i, item) ARRAY(ARRAY_INDEX($item, 3), ARRAY_INDEX($item, 4))), 
	ARRAY_SELECT($lookup, $(i, item) ARRAY(ARRAY_INDEX($item, 3), ARRAY_INDEX($item, 4)))
)

LET $functions:=
HASH(
	"GET_FINANCIAL_DELEGATE_IN_GROUP", $(target)
      IFERROR(
         (
		   LET $usersInGroup := 
           ARRAY_SELECT(
             EBMS_GET_DESCENDANT_ROLES($target), $(i, item) 
             ARRAY(
			   $item,
               EBMS_GET_ROLE_FINANCIAL_APPROVAL_LIMIT($item)
             )
           );

		   LET $usersWithDelegationAboveLimit := 
           ARRAY_WHERE(
             $usersInGroup, $(i, item) 
             AND(
               ARRAY_INDEX($item, 1) <> $target,
               ARRAY_INDEX($item, 1) <> "system",
               ARRAY_INDEX($item, 1) <> EBMS_GET_CURRENT_USER(),
               ARRAY_INDEX($item, 2) > [Submission Value]
             )
           );

           LET $sorted := 
		   ARRAY_SORT($usersWithDelegationAboveLimit, $(item) ARRAY_INDEX($item, 2));

		   RETURN ARRAY_FIRST(ARRAY_FIRST($sorted))
		), NULL()
  ),	"GET_USER_FROM_PARENT", $(target)
		IFERROR(
			DB_LOOKUP(
				$target, 
				ARRAY_INDEX([Parent Record], 1), 
				HASH("RecordID", ARRAY_INDEX([Parent Record], 2))
			), NULL()
		),


	"GET_USER_IN_ASSIGNMENT_FROM_SIGNIFICANT_PARENT", $(target)
		IFERROR(
			IF( 
				[Module] <> NULL, 
				(
					LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
					LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), [Module]);
					LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

					LET $result := 
					DB_LOOKUPALL(
						"Username in Role", //"Username in Role", 
						[Module], 
						HASH(
							"RecordID", $significantParentRecordID, 
							"Role", $target
						)
					);
					RETURN $result
				), 
				NULL
			), 
			NULL()
		),

    "ASSIGN_GROUP", $(target)
		$target,

	"GET_USER_IN_ROLE", $(target)
		//assumes there is only one child that is legitimate
		IFERROR(
			ARRAY_FIRST(
				ARRAY_WHERE(
					EBMS_GET_DESCENDANT_ROLES($target), $(i, item) 
					AND(
					  $item <> $target,
					 $item <> "system"
					)
				)
			), NULL()
		),

	"GET_USER_IN_POSITION", $(target)
		IFERROR(
			DB_LOOKUP(
				"Username", 
				"sys.Users",
				"GET_USER_POSITION([Username]) = " & DB_ENCODECONSTANT($target)
			),
			NULL()
		),

	"EXTERNAL_USER", $(target)
		IFERROR(
			$target,
			NULL()
		),

  	"GET_USER_IN_POSITION_AND_DEPARTMENT", $(target)
		//$target is an array of positions
		//departments check is inclusive of department and upwards of department
		IFERROR(
			DEPT_FIND_UPWARDS_FOR_DEPARTMENT(EBMS_GET_USER_DEPARTMENT([Submitted By]), $target), 
			NULL()
		),

  	"GET_USER_IN_POSITION_AND_SIGNIFICANT_PARENT_DEPARTMENT", $(target)
	IFERROR(
		LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
		LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), [Module]);
		LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

		LET $department := 
		DB_LOOKUP(
			"Department",
			[Module], 
			HASH("RecordID", $significantParentRecordID)
		);
		RETURN

		DEPT_FIND_UPWARDS_FOR_DEPARTMENT($department, $target),
	NULL()
	),

  	"GET_USER_SUPERVISOR", $()
  		IFERROR(
		  	ROLE(EBMS_GET_SUPERVISOR([Submitted By])),
			NULL()
		),

   "GET_USER_IN_GROUP_NAMED_DEPARTMENT_PLUS_CUSTOMSTRING", $(target)
		IFERROR(
			IF( 
				[Module] <> NULL, 
			    (
				  LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
				  LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), [Module]);
				  LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

  				  LET $department := 
				  DB_LOOKUP(
				  	"Department",
				  	[Module], 
				  	HASH("RecordID", $significantParentRecordID)
				  );

				  LET $groupName := $department & " - " & $target;
				  LET $groupExists := $groupName = ROLE($groupName); 

				  LET $approver := 
				  IF(
				  	  $groupExists = TRUE, ARRAY_FIRST(ARRAY_WHERE(EBMS_GET_DESCENDANT_ROLES($groupName), $(i, item) EBMS_CONTAINS($item, $groupName) <> TRUE)),
				      $groupExists <> TRUE, NULL 
				  );

				  LET $backupApprover := 
				  IF(
				  	  AND($groupExists = TRUE, $approver = NULL), 
					    ARRAY_FIRST(ARRAY_WHERE(
						  EBMS_GET_DESCENDANT_ROLES(EBMS_GET_ANCESTOR_ROLES($groupName)), $(i, item) 
						  EBMS_CONTAINS($item, $groupName) <> TRUE
						))
				  );
				  RETURN
				  IF(
					$approver <> NULL, 
					  $approver, 

					$backupApprover <> NULL, 
				      $backupApprover,
					NULL
				  )
			    )
			),
			NULL
		), 

  	"GET_USER_IN_GROUP_AND_IN_SIGNIFICANT_PARENT_DEPARTMENT", $(target)
		IFERROR(
			IF( 
				[Module] <> NULL, 
				(
					LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
					LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), [Module]);
					LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

					LET $department := 
					DB_LOOKUP(
						"Department", //"Username in Role", 
						[Module], 
						HASH("RecordID", $significantParentRecordID)
					);

					RETURN
					ARRAY_FIRST(
						ARRAY_WHERE(
							EBMS_GET_DESCENDANT_ROLES($target), $(i, item) 
							AND(
							   $item <> $target,
							   $item <> "system",
							   EBMS_GET_USER_DEPARTMENT($item) = $department
							)
						)
					)
				), 
				NULL
			), 
			NULL()
		),

  	"GET_PANEL_MEMBERS", $()
  		IFERROR(
		  	DB_LOOKUPALL(
			  	"Panel Member Username",
			  	"Evaluation",
			  	"[RecordID] = " & DB_ENCODECONSTANT([Parent RecordID]) & " AND [Panel Member Username] IS NOT EMPTY"
			),
			NULL()
		),
  "GET_VENDOR", $()
		IFERROR(
			LET $module := "Contract";
			LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
			LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), $module);
			LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

			LET $vendorRecordID := 
				DB_LOOKUP(
					"Vendor RecordID",
					$module,
					HASH(
						"RecordID", $significantParentRecordID
					)
				);

			LET $result :=
				DB_LOOKUPALL(
					"Username",
					"Vendor",
					"[RecordID] = " & DB_ENCODECONSTANT($vendorRecordID) & " AND [LineNumber] = 1"
			);
			RETURN "Vendor",//$result,
			NULL()
		)

);	

//----------------------------END SHORTCUTS----------------------------

LET $approverTypeDisplayName := 
HASH(
  "Panel Members", "Panel Member"
); 

LET $approvers :=
ARRAY_SELECTMANY(
    [Approver Types Required], $(i, item)
    LET $approverType := ARRAY_FIRST($item); //role
    LET $approvalType := ARRAY_INDEX($item, 2); // in system or docusign

    LET $data :=
    DB_LOOKUP(
        ARRAY("Method", "Arguments", "Who"),
        "Settings - Approval",
        "[Who] = " & DB_ENCODECONSTANT($approverType)
    );
    //ARRAY("GET_USER_IN_POSITION", "CEO")
    LET $method := ARRAY_INDEX($data, 1);
    LET $arguments := ARRAY_INDEX($data, 2);

	LET $user:= 
    COALESCE(
	  CALL(HASH_GET($functions, $method), $arguments), 
	  ARRAY(
        $approverType,
		$approverType,
		$approverType,
		$approverType
	  )
	);

    RETURN
	ARRAY_SELECT(
	  	$user, $(i, item) 
	  	ARRAY(
	    	//display name
		    IF( 
   				ISERROR(EBMS_GET_ROLE_DISPLAYNAME($item)), 
				  $approverType,
			    EBMS_GET_ROLE_DISPLAYNAME($item)
			), 	

			//username
		    IF( 
   				ISERROR($item),
			      "Not found",
			    $item
			),


			//role name
		    IF( 
          		ISERROR($item), 
            		$approverType & " - Role '" & $approverType & "' not defined. Refer to Approval Workflow settings.",
          		COALESCE(HASH_GET($approverTypeDisplayName, $approverType), $approverType)   
	    	), 
	    	$method, 	//method
		  	$approvalType //in system or docusign
		)	
	  )
	);

LET $escalateUsingThresholds := 
DB_LOOKUP(
	"User-Based Approval Thresholds", 
	"Settings - Approval", 
	"[RecordID] = 1"
);

LET $includeAllEscalationSteps := 
DB_LOOKUP(
	"Include Escalation Steps", 
	"Settings - Approval", 
	"[RecordID] = 1"
);

LET $escalate := $(approver, approverList, i)
(

	LET $displayName := 	ARRAY_INDEX($approver, 1); //display name
	LET $user := 			ARRAY_INDEX($approver, 2); //username
	LET $approverType:= 	ARRAY_INDEX($approver, 3); //role name
	LET $method := 			ARRAY_INDEX($approver, 4); //method
	LET $approvalType := 	ARRAY_INDEX($approver, 5); //in system or docusign

	LET $financialApprovalLimit := EBMS_GET_ROLE_FINANCIAL_APPROVAL_LIMIT($user); 

	LET $supervisor := ROLE(EBMS_GET_SUPERVISOR($user));

	LET $supervisorData := 
	ARRAY(
		EBMS_GET_ROLE_DISPLAYNAME($supervisor), 
		$supervisor, 
		COALESCE(EBMS_GET_USER_POSITION($supervisor), "Supervisor"), 
		"ESCALATE"
	);

	LET $newApproverList := ARRAY_CONCAT($approverList, ARRAY($supervisorData));

	LET $submissionAboveLimit := $financialApprovalLimit < [Submission Value];

	RETURN
	IF( 
		AND(
		   $submissionAboveLimit = TRUE, 
		   $supervisor <> NULL(),
		   $supervisor <> $user
		),

		CALL(
			$escalate, 
			$supervisorData, 
			$newApproverList, 
			$i + 1
		),

		IF( 
			$includeAllEscalationSteps = TRUE, 
			$approverList, 
			ARRAY(ARRAY_LAST($approverList))
		)
	)
);

LET $escalations := 
IF( 
	$escalateUsingThresholds = TRUE, 
	CALL(
		$escalate, 
		ARRAY_LAST($approvers), 
		ARRAY(ARRAY_LAST($approvers)), 
		1
	)
);

LET $result :=  
ARRAY_CONCAT(
  $approvers, 
  $escalations
);

RETURN 
ARRAY_WHERE(
  $result, $(i, item) 
  $item <> NULL()
)

LET $types := 
ARRAY_INDEX(
  ARRAY_TRANSPOSE(
	[Approver Types Required]
  ), 2
);

RETURN
ARRAY_INDEXOF($types, "DocuSign") > 0

ARRAY_INDEX(
  ARRAY_TRANSPOSE(
	[Approver Types Required]
  ), 2
)

LET $approvers := [Approver Type to User Map];

LET $checkList := 
ARRAY_SELECT(
  $approvers, $(i, item) 
  ARRAY(
	ARRAY_INDEX($item, 2), 
	ARRAY_LAST($item), 
	ARRAY_INDEX($item, 3)
  )
); //username and approvaltype

LET $distinct := 
ARRAY_WHERE(
	$approvers, $(i, item)
	LET $arrayToCheck := ARRAY_SKIP($checkList, $i);
	LET $lastOccurence :=  ARRAY_INDEXOF($arrayToCheck, ARRAY(ARRAY_INDEX($item, 2), ARRAY_LAST($item), ARRAY_INDEX($item, 3))) = NULL();
	LET $isOriginator := ARRAY_INDEX($item, 2) = [Originator];

	RETURN
	AND(
		$lastOccurence = TRUE, 
		$isOriginator <> TRUE
	)
);

RETURN
ARRAY_TRANSPOSE($distinct)

LET $hash := 
HASH(
  "EXTERNAL_USER", "External"
);

RETURN
IF( 
  [Approver Data] = ARRAY(), 
  ARRAY("System User"), 
  ARRAY_SELECT(
    ARRAY_INDEX([Approver Data], 5), $(i, item) 
    COALESCE(HASH_GET($hash, $item), "System User")
  )
)
