FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $vendorData := HASH_GET([Meaningful Award Data], "Vendor Data"); 
LET $contractData := HASH_GET([Meaningful Award Data], "Contract Award Data"); 
LET $subcontractID:= ARRAY_FIRST([Temp]);

LET $newValues :=
HASH(
  "Potential Vendor RecordID", 		HASH_GET([Meaningful Award Data], "Vendor RecordID"),
  "Potential Vendor Description", 	HASH_GET($vendorData, "Company Name"),
  "Subcontract RecordID", 			$subcontractID,
  "Subcontract Start Date", 		HASH_GET($contractData, "Start Date"),
  "Subcontract End Date", 			HASH_GET($contractData, "Original End Date"),
  "Subcontract Value", 				HASH_GET($contractData, "Initial Contract Value Excl. Tax")
);

LET $justNewValues := ARRAY_SELECT(HASH_KEYS($newValues), $(k, key) HASH_GET($newValues, $key)); 

LET $columns := HASH_KEYS($newValues);

LET $data := 
ARRAY_TRANSPOSE(DB_LOOKUPALL(
  $columns,
  "Contract",
  "[Potential Vendor RecordID] IS NOT EMPTY AND [RecordID] = " & ARRAY_FIRST([Created Head Agreement RecordID])
)); 

LET $combinedData := ARRAY_SELECT($data, $(i,item) ARRAY_CONCAT($item, HASH_GET($newValues, ARRAY_INDEX($columns, $i))));

LET $hashed := 
INDEXES_TO_KEYS(
  IF($combinedData = ARRAY(), $justNewValues, $combinedData),
  $columns
); 

RETURN $hashed

