LET $opportunityOfferKeys := 	HASH_GET($VPResponseData, "Offer Key"); 

LET $opportunityValuesHashed :=  INDEXES_TO_KEYS($opportunityValues, $opportunityKeys); 
LET $opportunityOfferValuesHashed := INDEXES_TO_KEYS($opportunityOfferValues, $opportunityOfferKeys); 

LET $VPRindicatedCost := 		HASH_GET($opportunityValuesHashed, "indicatedCost");
LET $VPRofferExclTax := 		HASH_GET($opportunityOfferValuesHashed, "exTax");
LET $VPRofferInclTax := 		HASH_GET($opportunityOfferValuesHashed, "incTax");

LET $VPRvalueExclTax := 		IF( OR( $VPRindicatedCost <0, $VPRindicatedCost = $VPRofferInclTax), $VPRofferExclTax, $VPRindicatedCost); // $VPRindicatedCost  is set to -1 when it's not set.
LET $VPRvalueInclTax := 		IF( OR( $VPRindicatedCost <0, $VPRindicatedCost = $VPRofferInclTax), $VPRofferInclTax, $VPRindicatedCost);
LET $VPRtax := 					$VPRvalueInclTax - $VPRvalueExclTax;

//End Details from VP Response

//--Vendor Details from VP Response
LET $VPRopportunityId := 		ARRAY_FIRST(HASH_GET($VPResponseData, "Opportunity Id"));
LET $VPRresponseId := 			ARRAY_FIRST(HASH_GET($VPResponseData, "Response Id"));
LET $VPRvendorValues := 		HASH_GET($VPResponseData, "Vendor Value"); 
LET $VPRvendorKeys := 			HASH_GET($VPResponseData, "Vendor Key"); 

LET $VPNvendorValuesHashed := 		INDEXES_TO_KEYS($VPRvendorValues, $VPRvendorKeys); 
LET $VPRcompanyName := 				HASH_GET($VPNvendorValuesHashed, "name");
LET $VPRtradingName := 				HASH_GET($VPNvendorValuesHashed, "name");
LET $VPRstreetAddress := 			HASH_GET($VPNvendorValuesHashed, "address"); 
LET $VPRsuburb := 					HASH_GET($VPNvendorValuesHashed, "citySuburb");  
LET $VPRstate := 					HASH_GET($VPNvendorValuesHashed, "stateRegionName"); 
LET $VPRpostcode := 				HASH_GET($VPNvendorValuesHashed, "postZip"); 
LET $VPRcountry := 					HASH_GET($VPNvendorValuesHashed, "country"); 
LET $VPRbusinessNumber := 			HASH_GET($VPNvendorValuesHashed, "number"); 
LET $VPRbusinessNumberType := 		HASH_GET($VPNvendorValuesHashed, "numberTypeName"); 
//--End Vendor Details from VP Response

//--Vendor Details from Vendor record
LET $VENDcompanyName := 			ARRAY_FIRST(HASH_GET($vendorData, "Company Name"));
LET $VENDtradingName := 			ARRAY_FIRST(HASH_GET($vendorData, "Trading Name"));
LET $VENDstreetAddress := 			ARRAY_FIRST(HASH_GET($vendorData, "Street Address")); 
LET $VENDsuburb := 					ARRAY_FIRST(HASH_GET($vendorData, "Suburb"));  
LET $VENDstate := 					ARRAY_FIRST(HASH_GET($vendorData, "State")); 
LET $VENDpostcode := 				ARRAY_FIRST(HASH_GET($vendorData, "Postcode")); 
LET $VENDcountry := 				ARRAY_FIRST(HASH_GET($vendorData, "Country")); 
LET $VENDbusinessNumber := 			ARRAY_FIRST(HASH_GET($vendorData, "BusinessNumber")); 
LET $VENDbusinessNumberType := 		ARRAY_FIRST(HASH_GET($vendorData, "Business Number Type")); 
//--End Vendor Details from VP Response


//Vendor Record
LET $vendorRecordID := 
IF(
  AND(
    [Selection Entity Type] = "Vendor",
    [Selection RecordID]#$line <> NULL
  ), 
  [Selection RecordID]#$line,

  DB_LOOKUP(
    "RecordID", 
    "Vendor", 
    "([BusinessNumber] IS NOT EMPTY AND [BusinessNumber] = " & DB_ENCODECONSTANT($VPRbusinessNumber) & ")" & 
    " AND " & 
    "([Company Name] IS NOT EMPTY AND [Company Name] = " & DB_ENCODECONSTANT($VPRcompanyName) & ")"
  )
); 
//--End Vendor Record

//--Procurement Data
LET $PPrecordID := 				ARRAY_FIRST(HASH_GET($procurementData, "RecordID")); 
LET $PPstartDate := 			ARRAY_FIRST(HASH_GET($procurementData, "Start Date")); 
LET $PPoutput := 				ARRAY_FIRST(HASH_GET($procurementData, "Output")); 
LET $PPoutputPCategory := 		ARRAY_FIRST(HASH_GET($procurementData, "Output Procurement Category")); 
LET $PPheadAgreementId := 		ARRAY_FIRST(HASH_GET($procurementData, "Head Agreement RecordID")); 
LET $PPcontractType := 			ARRAY_FIRST(HASH_GET($procurementData, "Contract Type")); 
LET $PPdepartment := 			ARRAY_FIRST(HASH_GET($procurementData, "Department"));
LET $PPcategory :=				IF(ARRAY_LENGTH(ARRAY_WHERE(HASH_GET($procurementData, "Category"), $(i, item) $item <> NULL())) = 1, ARRAY_FIRST(HASH_GET($procurementData, "Category")), NULL);
LET $PPsubcategory :=			IF(ARRAY_LENGTH(ARRAY_WHERE(HASH_GET($procurementData, "Subcategory"), $(i, item) $item <> NULL())) = 1, ARRAY_FIRST(HASH_GET($procurementData, "Subcategory")), NULL);
LET $PPcostCentre := 			ARRAY_FIRST(HASH_GET($procurementData, "Cost Centre")); 
LET $PPdurationYears := 		ARRAY_FIRST(HASH_GET($procurementData, "Duration Years")); 
LET $PPdurationMonths := 		ARRAY_FIRST(HASH_GET($procurementData, "Duration Months")); 
LET $PPdurationDays := 			ARRAY_FIRST(HASH_GET($procurementData, "Duration Days")); 
LET $PPoriginalEndDate := 		ARRAY_FIRST(HASH_GET($procurementData, "Original End Date")); 
LET $PPextensionYears := 		ARRAY_FIRST(HASH_GET($procurementData, "Extension Years")); 
LET $PPextensionMonths := 		ARRAY_FIRST(HASH_GET($procurementData, "Extension Months")); 
LET $PPextensionDays := 		ARRAY_FIRST(HASH_GET($procurementData, "Extension Days")); 
LET $PPextensionOptions := 		ARRAY_FIRST(HASH_GET($procurementData, "Extension Options")); 
LET $PPcontractTitle := 		ARRAY_FIRST(HASH_GET($procurementData, "Procurement Title")); 
LET $PPscopeOfWork := 			ARRAY_FIRST(HASH_GET($procurementData, "Scope of Work")); 
LET $PPbackground := 			ARRAY_FIRST(HASH_GET($procurementData, "Procurement Background")); 
LET $PPestimatedValue := 		ARRAY_FIRST(HASH_GET($procurementData, "Estimated Value")); 
LET $PPpriceModel := 			ARRAY_FIRST(HASH_GET($procurementData, "Price Model")); 
LET $PPledgerNumber := 			ARRAY_FIRST(HASH_GET($procurementData, "Ledger Number")); 
LET $PPprocurementManager :=	ARRAY_FIRST(HASH_GET($procurementData, "Procurement Manager")); 
LET $PPprocurementOfficer :=	ARRAY_FIRST(HASH_GET($procurementData, "Procurement Officer"));
LET $PPprocurementOwner :=		ARRAY_FIRST(HASH_GET($procurementData, "Procurement Owner"));


LET $PProle := 					ARRAY_WHERE(HASH_GET($procurementData, "Role"), $(i, item) $item <> NULL()) ;
LET $PPusernameInRole	:=		ARRAY_WHERE(HASH_GET($procurementData, "Username in Role"), $(i, item) $item <> NULL());
LET $PPcustomRole :=			ARRAY_WHERE(HASH_GET($procurementData, "Custom Role"), $(i, item) $item <> NULL());
LET $PProleNotASystemUser :=	ARRAY_WHERE(HASH_GET($procurementData, "Role is not a System User"), $(i, item) $item <> NULL());
LET $PPpersonInRole :=			ARRAY_WHERE(HASH_GET($procurementData, "Person in Role"), $(i, item) $item <> NULL());	
//--End Procurement Data

//--Evaluation Data
LET $EVALtitle :=		ARRAY_FIRST(HASH_GET($evaluationData, "Title"));
LET $EVALprices := 		HASH_GET($evaluationData, "Price");
LET $EVALrespondents := HASH_GET($evaluationData, "Respondent UUID");
//End Evaluation Data

//Final Data
//--Award data
LET $startDate := 	COALESCE($PPstartDate, $VPOawardDate); 
LET $title := 		COALESCE($VPOname, $PPcontractTitle, $EVALtitle); 
LET $summary := 	COALESCE($VPOdescription, $PPscopeOfWork); 

//--Vendor data
LET $vendorCompanyName := 			COALESCE($VENDcompanyName, $VPRcompanyName);
LET $vendorTradingName := 			COALESCE($VENDtradingName, $VPRtradingName);
LET $vendorStreetAddress := 		COALESCE($VENDstreetAddress, $VPRstreetAddress);
LET $vendorSuburb := 				COALESCE($VENDsuburb, $VPRsuburb);
LET $vendorPostcode := 				COALESCE($VENDpostcode, $VPRpostcode);
LET $vendorState := 				COALESCE($VENDstate, $VPRstate);
LET $vendorCountry := 				COALESCE($VENDcountry, $VPRcountry);
LET $vendorBusinessNumber := 		COALESCE($VENDbusinessNumber, $VPRbusinessNumber);
LET $vendorBusinessNumberType := 	COALESCE($VENDbusinessNumberType, $VPRbusinessNumberType);

//-- Output Data:
LET $output := 
IF(
  	$PPoutput = "Contract", "Standalone Contract",
  	$PPoutput = "Subcontract", "Subcontract",
  	$PPoutput = "Panel Contract", "Panel Contract",
  	$PPoutput = "Procurement", "Procurement",
  	$PPoutput = "Purchase Order", "Purchase Order",
  	"Standalone Contract"
  );

//End Final Data

RETURN 
HASH(
  	"Line", HASH_GET($eventargs, "LineNumber"),

  "Output", 						$output,
  "Head Agreement RecordID",		$PPheadAgreementId,

  "Vendor RecordID", 				$vendorRecordID, 

  "Vendor Exists", 					$vendorRecordID <> NULL, 

  "Vendor Data",
   HASH(
     "Created from VP Opportunity ID",  COALESCE($VPRopportunityId),
     "Company Name",           		    $vendorCompanyName,
     "Trading Name",           		    $vendorTradingName,
     "Street Address",         		    $vendorStreetAddress,
     "Suburb",                 		    $vendorSuburb,
     "State",                  		    $vendorState,
     "Postcode",        		   	 	$vendorPostcode,
     "Country",                		    $vendorCountry,
     "BusinessNumber",         		    $vendorBusinessNumber,
     "Business Number Type", 		    $vendorBusinessNumberType, 
     "Vendor Registered", 			    TRUE,
     "Source", 						    IF($VPRresponseId <> NULL, "VendorPanel")
    ), 

  "PO Agreement Award Data",
  HASH(
	"Source EFormType",					[Context EFormType],		
	"Source RecordID",					[Context RecordID],		
	"Title",							$title,		
	"Scope",							$summary,
	"VP Opportunity ID", 				$VPRopportunityId
  ),

  "Purchase Order Award Data", 
  HASH(
	"Source EFormType",					[Context EFormType],		
	"Source RecordID",					[Context RecordID],		
	"PO Agreement Title",				$title,	
	"Overall Budget", 					$VPRvalueInclTax,
	//"Vendor",			$vendorRecordID,
	"Vendor Name",						$vendorCompanyName,
	"VP Opportunity ID", 				$VPRopportunityId,		
    "VP Response ID", 					$VPRresponseId
  ),

  "Procurement Award Data", 
  HASH(
	"Head Agreement RecordID",		$PPheadAgreementId,
	"Procurement Title",			$title,		
	"Procurement Background",		$PPbackground,			
	"Scope of Work",				$summary,	
	"Department",					$PPdepartment,
	"Estimated Value",				COALESCE($VPObudget, $PPestimatedValue),
	"Price Model",					$PPpriceModel,
	"Cost Centre",					$PPcostCentre,
	"Ledger Number",				$PPledgerNumber,
	"Procurement Category",			$PPoutputPCategory,	

	"Role",							$PProle,
	"Username in Role",				$PPusernameInRole,
	"Custom Role",					$PPcustomRole,
	"Role is not a System User",	$PProleNotASystemUser,
	"Person in Role",				$PPpersonInRole,

	"Contract Type",				$PPcontractType,
	"VP Opportunity ID", 			$VPRopportunityId
	//"Vendor RecordID", 
	//"Head Agreement RecordID"
	//"Output"
	//"Procurement Category",
  ),

  "Head Agreement Award Data", 
   HASH(
    "Procurement RecordID", 			$PPrecordID,
	"Source", 							IF($VPRresponseId <> NULL, "VendorPanel"),
    "Assignee", 						ROLE(IF($VPRresponseId <> NULL, ARRAY_FIRST(HASH_GET($VPResponseData,"Pushed by")),"Contract Setup Processing")), //If request was pushed from GTM assigns the user who pushed it; otherwise assigns "Contract Setup Processing"
	"Contract Type", 					$PPcontractType,
	"Contract Title", 					$title, 
	"Description", 						$title,
    "Contract Summary", 				$summary,
	"Department", 						$PPdepartment,
	"Contract Category",				$PPcategory,
	"Contract Subcategory",				$PPsubcategory,
	"Cost Center", 						$PPcostCentre,

	"Procurement Reference", 			IF($VPRresponseId <> NULL, "VP" & $VPRopportunityId),
    "VP Opportunity ID", 				$VPRopportunityId,
	"External Reference Number",		$VPOexReferenceNumber
   // "VP Response ID", 				$VPRresponseId
   ), 

  "Subcontract Award Data",
  HASH(
	"Subcontract Number",				$maxSubcontractNumber+1,
	"Contract Number",					$headAgreementNumber& "-"& $maxSubcontractNumber+1,
	),

  "Contract Award Data", 
  HASH(
    "Procurement RecordID", 			$PPrecordID,				
	"Contract Title", 					$title,
	"Description", 						$title & IF( $vendorCompanyName <> NULL, " (" & $vendorCompanyName &")"),
    "Contract Summary", 				$summary,
    "Award Date", 						COALESCE($VPOawardDate, NOW()),
    "Start Date", 						$startDate, 
    "Initial Contract Value Excl. Tax", $VPRvalueExclTax, 
    "Initial Contract Value Incl. Tax", $VPRvalueInclTax, 
    "Tax", 								$VPRtax, 

    //"Vendor RecordID", 				$vendorRecordID, 
    "Vendor Name", 						$vendorCompanyName,
    "BusinessNumber", 					$vendorBusinessNumber,	
    "VP Opportunity ID", 				$VPRopportunityId,		
    "VP Response ID", 					$VPRresponseId,	
	"External Reference Number",		$VPOexReferenceNumber,
    "Source", 							IF($VPRresponseId <> NULL, "VendorPanel"),
    "Assignee", 						ROLE(IF($VPRresponseId <> NULL, ARRAY_FIRST(HASH_GET($VPResponseData,"Pushed by")),"Contract Setup Processing")), //If request was pushed from GTM assigns the user who pushed it; otherwise assigns "Contract Setup Processing",
    "Procurement Reference", 			IF($VPRresponseId <> NULL, "VP" & $VPRopportunityId),			
    "Tax Mode", 						"Value",

    "Contract Type", 					$PPcontractType,	
    "Cost Center", 						$PPcostCentre,
    "Department", 						$PPdepartment,
	"Contract Category",				$PPcategory,
	"Contract Subcategory",				$PPsubcategory,
    "Duration Years", 					$PPdurationYears,	
    "Duration Months", 					$PPdurationMonths,	
    "Duration Days", 					$PPdurationDays,	
    "Original End Date",				$PPoriginalEndDate,		
    "Extension Days", 					$PPextensionDays,	
    "Extension Months", 				$PPextensionMonths,		
    "Extension Years", 					$PPextensionYears,	
    "Extension Options", 				$PPextensionOptions	
   ) 
)

TriggerCustomEvent.EventNameTemplate:
{
  IF( 
	OR(
	  [Award Output Override] = "Standalone Contract",
	  //this is a fallback for if the award would be to add a subcontract to an existing head agreement, but the head agreement recordID hasn't been specified
	  AND(
	    [Head Agreement RecordID] = NULL,
	    [Award Output Override] = "Subcontract",
      )
    ), 
	"Award - Standalone Contract",

	//only gets through if a head agremeent recordid has been provided
	AND(
	  [Head Agreement RecordID] <> NULL,
	  [Award Output Override] = "Subcontract",
    ), 
	"Award - Subcontract",

	[Award Output Override] = "Panel Contract",
	"Award - Panel Contract",

	[Award Output Override] = "Procurement",
    "Award - Procurement",

	[Award Output Override] = "Purchase Order",
    "Award - Purchase Order"
  )
}

FieldEntry.Formula:
LET $existing:= target.[Awarded Entities];
LET $awarded := EACH [Award EFormType];

RETURN ARRAY_DISTINCT(
  ARRAY_CONCAT($existing, $awarded)
  )

ARRAY_DISTINCT(
	ARRAY_WHERE(
		ARRAY_CONCAT(
			 target.[Stage List], 
    		ARRAY("Award")
		), $(i,item)
		$item <> NULL()
	)
)

ARRAY_DISTINCT(
	ARRAY_WHERE(
		ARRAY_CONCAT(
			 target.[Stages Complete], 
    		ARRAY("Award")
		), $(i,item)
		$item <> NULL()
	)
)

LET $new := HASH("Award", NOW()); 
LET $existing := target.[Stage Completion Data];

RETURN 
HASH_MERGE(
  $existing, $new
)
