FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $targetData := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH target.[Role], 
		EACH target.[Person in Role], 
		EACH target.[Username in Role], 
		EACH target.[Change of Role Status],
		EACH target.[Role is not a System User]
	)
);

LET $targetLine := 
ARRAY_INDEXOF(
	ARRAY_SELECT(
		$targetData, $(i, item) 
		ARRAY_INDEX($item, 1) == [Role]
	),
	TRUE
);

LET $buildArray := $(array, newValue)(
	LET $before := 
	IF(
		$array = ARRAY(),
		ARRAY_SELECT(RANGE(0, $targetLine - 1), $(i, item) NULL()),
		ARRAY_TAKE($array, $targetLine - 1)
	);

	LET $before := 
	IF(
		ARRAY_LENGTH($before) < $targetLine - 1, 
		ARRAY_PAD($before, $targetLine - 1),
		$before
	);

	RETURN
	ARRAY_CONCAT(
		$before,
		$newValue,
		ARRAY_SKIP($array, $targetLine) 
	)
);

RETURN
HASH(
	"Person in Role", 		CALL($buildArray, EACH target.[Person in Role], 			IF([New Role is not System User] == TRUE, [New Person in Role])),
	"Username in Role", 		CALL($buildArray, EACH target.[Username in Role], 		IF([New Role is not System User] <> TRUE, [New User in Role])),
	"Change of Role Status", 	CALL($buildArray, EACH target.[Change of Role Status], 	[Status]),
	"Role is not a System User", 	CALL($buildArray, EACH target.[Role is not a System User], 	[New Role is not System User])
)

DisplayMessage.MessageTemplate:
Change has been applied, refresh the record to see the new allocations. 
{ARRAY_WHERE(
  ARRAY_CONCAT(
  	ARRAY(ARRAY([Parent EFormType], [Parent RecordID])),
	[Allocation]
	),
  $(i,item)
  AND( $item <> NULL, $item <> ARRAY())
)}
