Column.DefaultValueFormula:
LET $currentRoles := EBMS_GET_ANCESTOR_ROLES(EBMS_GET_CURRENT_USER());
/*  ARRAY_SELECT(
    EBMS_GET_ANCESTOR_ROLES(EBMS_GET_CURRENT_USER()), $(i, item) 
    ROLE($item)
  ); */

LET $currentUser := ARRAY_FIRST($currentRoles); 

LET $userGroupMapping:= 
  INDEXES_TO_KEYS(
    ARRAY_SELECT($currentRoles, $(i, item) TRUE),
    $currentRoles
  );  

LET $lookup := 
  DB_LOOKUPALL(
  	ARRAY(
  		"Module Name", 
  		"Module Settings EForm",
  	  	"Module Access",
	  	//header fields
	  	"Automation in Navigation",
	  	"Global Settings in Navigation"
  	), 
  	"Global Settings", 
  	"[RecordID] FAST_EQUAL_TO 1 AND [Module Name] IS NOT EMPTY AND [Module Type] FAST_EQUAL_TO 'Module'"
  );


LET $modules:= ARRAY_SELECT($lookup, $(i,item) ARRAY_TAKE($item, 3));

LET $data :=
ARRAY_SELECT(
	$modules, $(m, module)

	LET $moduleName 		:= 	ARRAY_INDEX($module, 1); 
	LET $moduleSettings 		:=	ARRAY_INDEX($module, 2); 

	LET $settingPage 	:=	
	"records/" & URLENCODE($moduleSettings) & "&[RecorDID]=1"; 

	LET $accessList := ARRAY_INDEX($module, 3); 

	LET $hasAccess :=
	ARRAY_LENGTH(
		ARRAY_WHERE(
			$accessList, $(i, item) 
			ARRAY_LENGTH(
				ARRAY_WHERE(
					$item, $(i, item) 
				  	HASH_GET($userGroupMapping, $item)= TRUE
				)
			) > 0
		)
	) > 0;

	RETURN
	IF( 
		$hasAccess == TRUE, 
		ARRAY(
			$moduleName,
			$moduleSettings
		)
	)
);


LET $extras:= 
INDEXES_TO_KEYS(
  ARRAY_SKIP( ARRAY_FIRST($lookup), 3),
  ARRAY("Automation in Navigation", "Global Settings in Navigation")
  );

LET $modulesPlus:= 
ARRAY_CONCAT(
  	$data, 
  	IF( HASH_GET( $extras, "Global Settings in Navigation") = TRUE, ARRAY(ARRAY("Settings - Global", "Global Settings"))),
	IF( HASH_GET( $extras, "Automation in Navigation") = TRUE, ARRAY(ARRAY("Settings - Automation", "Settings - Automation")))
);


RETURN 
ARRAY_WHERE(
			$modulesPlus, $(i, item) $item <> NULL()
		)

Binding.Expression:
LET $set := [Module Data];

RETURN
ARRAY_SELECT(
	$set,	  
  	$(i, item) 
	  LET $name := ARRAY_INDEX($item, 1); 
	  LET $eform := ARRAY_INDEX($item, 2); 

	  RETURN
	  HASH(
		  "value", 	$eform,
		  "text", 	IF($eform = "Global Settings", $name, $eform)//$name
	  )
)

FieldEntry.Formula:
LET $supportedWorkflows :=
	ARRAY(
	  	"Contract Spend",
	  	"Purchase Order Spend"
	);

LET $disabledByDefault :=
	ARRAY_SELECT($supportedWorkflows, $(i, item) FALSE);

RETURN ARRAY($supportedWorkflows, $disabledByDefault)
