Binding.Expression:
LET $value := [Additional Setup Information]#[LineNumber];

LET $displayName :=
IF(
  	$value = "Questionnaire Data",
  	"Custom Reporting Fields",

  	$value
);

RETURN $displayName

FieldEntry.Formula:
LET $questionnaireEnabled :=
DB_LOOKUP(
  	"Supplementary Data Questionnaire Enabled",
  	"Settings - Contract",
  	"[RecordID] FAST_EQUAL_TO 1 AND [LineNumber] FAST_EQUAL_TO 1"
);

LET $risksEnabled := ARRAY_INDEXOF(EACH [Profile Register], "Risks") > 0;

LET $allInfo :=
ARRAY(
  IF($questionnaireEnabled, ARRAY("Questionnaire Data", TRUE)),
  IF($risksEnabled, ARRAY("Risks", FALSE))
);

LET $filtered := ARRAY_WHERE($allInfo, $(i,item) ARRAY_INDEX($item,1) <> NULL);

LET $existingInfo :=
INDEXES_TO_KEYS(
  	EACH [Additional Setup Information Enabled],
  	EACH [Additional Setup Information]
);

RETURN

ARRAY_SELECT(
  	$filtered, $(i,item)
  	LET $info := ARRAY_INDEX($item, 1);
  	LET $defaultValue := ARRAY_INDEX($item, 2);

  	RETURN

  	ARRAY(
	  	$info,
	  	IF(
		  	ARRAY_INDEXOF(HASH_KEYS($existingInfo), $info) > 0, //check if function already exists in array
		  	HASH_GET($existingInfo, $info), //get current value of function
		  	$defaultValue //fallback to default value if not found
		)
	)
)

LET $allActions :=
ARRAY(
  //"Details Summary", 
  "Duration Summary", 
  "Simple Value Summary",
  "Value Summary",
  "Variations",
  //"Roles Summary",
  "Contract Events",
  "Procurement Details",
  "Related Records"//,
  //"Vendor Tab"  
);

LET $existingActions := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Block],
		EACH [Block Enabled]
	)
);

LET $newActions := 
ARRAY_SELECT(
	ARRAY_WHERE(
		$allActions, $(a, action)
		ARRAY_INDEXOF(
			ARRAY_INDEX(ARRAY_TRANSPOSE($existingActions), 1), 
			$action
		) = NULL()
	), 
	$(a, action)
  	IF(
		$action = "Simple Value Summary",
	  	ARRAY($action, FALSE), //Disable Simple Value Summary by default

	  	ARRAY($action, TRUE)
	)
);

LET $actionsStaying := 
ARRAY_WHERE(
	$existingActions, $(a, action)
	ARRAY_INDEXOF(
		$allActions, 
		ARRAY_INDEX($action, 1)
	) > NULL()
);

LET $updatedActionsList :=
ARRAY_CONCAT(
	$actionsStaying,
	$newActions
);

RETURN $updatedActionsList

LET $allFunctions :=
ARRAY(
  ARRAY("High Level Details", TRUE),
  ARRAY("Vendor", TRUE),
  ARRAY("Procurement Details", TRUE),
  ARRAY("Value", TRUE),
  ARRAY("Duration", TRUE),
  ARRAY("Roles", TRUE)
);

LET $existingFunctions :=
INDEXES_TO_KEYS(
  	EACH [Function Enabled],
  	EACH [Function]
);

RETURN

ARRAY_SELECT(
  	$allFunctions, $(i,item)
  	LET $function := ARRAY_INDEX($item, 1);
  	LET $defaultValue := ARRAY_INDEX($item, 2);

  	RETURN

  	ARRAY(
	  	$function,
	  	IF(
		  	ARRAY_INDEXOF(HASH_KEYS($existingFunctions), $function) > 0, //check if function already exists in array
		  	HASH_GET($existingFunctions, $function), //get current value of function
		  	$defaultValue //fallback to default value if not found
		)
	)
)

LET $allActions :=
DB_LOOKUPALL(
	"Contract Function Name", 
	"Settings - Contract",
	"[Contract Function Enabled] = TRUE"
);
/*LET $append:=
ARRAY(
 	"Vendor Insurance",
  	"Vendor Contact"
  );
*/
LET $allActions:= $allActions; //ARRAY_CONCAT($allActions, $append);

LET $existingActions := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Register],
		EACH [Register Enabled]
	)
);

LET $newActions := 
ARRAY_SELECT(
	ARRAY_WHERE(
		$allActions, $(a, action)
		ARRAY_INDEXOF(
			ARRAY_INDEX(ARRAY_TRANSPOSE($existingActions), 1), 
			$action
		) = NULL()
	), 
	$(a, action)
	ARRAY($action, TRUE)
);

LET $actionsStaying := 
ARRAY_WHERE(
	$existingActions, $(a, action)
	ARRAY_INDEXOF(
		$allActions, 
		ARRAY_INDEX($action, 1)
	) > NULL()
);

LET $updatedActionsList :=
ARRAY_CONCAT(
	$actionsStaying,
	$newActions
);

RETURN $updatedActionsList
