Column.Formula:
LET $defaultBusinessNumberType := 
HASH_GET(
  ARRAY_FIRST(
    ARRAY_WHERE(
      HASH_GET([Vendor Module Settings], "MultilineData"), $(i, item)
      HASH_GET($item, "Default Business Number Type") = TRUE
    )
  ), 
  "Business Number Type"
); 

RETURN
IF( 
  [Business Number Type] = NULL(), 
  $defaultBusinessNumberType, 
  [Business Number Type]
)

LET $profileFunctions := HASH_GET([Contract Module Settings], "Profile Supported Functions");
LET $systemEnabled := HASH_GET([Contract Module Settings], "Contract Function Hash");
LET $tabRegisterMap := HASH_GET([Contract Module Settings], "Contract Function Map"); 
LET $contractTypeEnabledRegisters := HASH_GET([Contract Type Settings], "Enabled Registers"); 
LET $vendorTabAvailable := HASH_GET([Contract Type Settings], "Vendor Tab Available"); 
LET $enabledBlocks := HASH_GET([Contract Type Settings], "Enabled Blocks");

LET $manualRegisterMode := 
AND(
  [Register Selection Mode] = "Manual",
  ARRAY_INDEXOF($profileFunctions, "Manage Registers") > 0
); 

LET $tabAccess := HASH_GET([Contract Module Settings], "Contract Tab Access Hash");

LET $tabs := HASH_KEYS($tabRegisterMap); 

LET $tabsWithRegistersEnabled := 
ARRAY_WHERE(
  $tabs, $(t, tab) 

  LET $tabRegisters := HASH_GET($tabRegisterMap, $tab);

  LET $userHasTabAccess := 
  ARRAY_LENGTH(ARRAY_WHERE(
    HASH_GET($tabAccess, $tab), $(i, item) 
    ARRAY_INDEXOF([User to Group Mappings], T($item))
  )) > 0;

  RETURN 
  ARRAY_LENGTH(
	ARRAY_WHERE(
	  $tabRegisters, $(r, register) 
	  IF( 
		$manualRegisterMode = TRUE, 
		  AND(
		    //ARRAY_INDEXOF($contractTypeEnabledRegisters, $register) > 0,
		    ARRAY_INDEXOF([Register Override], $register) > 0, 
			$userHasTabAccess = TRUE
		  ),

		AND(
          HASH_GET($systemEnabled, $register) = TRUE,
	  	  ARRAY_INDEXOF($profileFunctions, $register) > 0, 
	  	  ARRAY_INDEXOF($contractTypeEnabledRegisters, $register) > 0, 
	  	  $userHasTabAccess = TRUE
		)
	  )
    )
  ) > 0
);

