LET $fullSet := 
ARRAY_CONCAT(
  "Summary",
  "Overview",
  IF([Type] = "Head Agreement", "Subcontracts"),
  IF(
	AND(
	  [Vendor RecordID] > 0, 
	  $vendorTabAvailable = TRUE
	), 
	"Vendor"
  ),
  $tabsWithRegistersEnabled,
  IF(
    OR([Status] = "Contract Closed", [Status] = "Expired"),
    "Closure"
  )
);

LET $vendorSet  := 
ARRAY_WHERE(
  ARRAY(
	"Overview",
	IF([Vendor RecordID] > 0, "Vendor"),
	IF(ARRAY_LENGTH([Enabled Vendor Actions]) > 0, "Registers")
  ), $(i, item)
  $item <> NULL()
);

LET $fullSet := 
ARRAY_WHERE(
  ARRAY_DISTINCT($fullSet), $(i, item) 
  $item <> NULL()
);

RETURN 
IF( 
  [User is Vendor] = TRUE, 
    $vendorSet, 

  [Status] <> "Setup", 

  ARRAY_SELECT(
    $fullSet, $(i,item)
    HASH(
  	  "value", $item,
  	  "text", IF($item = "Vendor", [Second Party], $item = "Overview", "Details", $item)
    )
  ), 

  ARRAY()
)

LET $defaultBusinessNumberType := 
HASH_GET(
  ARRAY_FIRST(
    ARRAY_WHERE(
      HASH_GET([Vendor Module Settings], "MultilineData"), $(i, item)
      HASH_GET($item, "Default Business Number Type") = TRUE
    )
  ), 
  "Business Number Type"
); 

RETURN 
IF( 
  [New Business Number Type] = NULL(), 
  $defaultBusinessNumberType, 
  [New Business Number Type]
)

