LET $approvalRequired := 
NOT(DB_LOOKUP(
  "Not Required", 
  "Approval Workflow", 
  HASH("Submission Type", "Contract Initiation", "LineNumber", 1)
)); 

LET $approvalsFeatureEnabled := 
ARRAY_INDEXOF(
  DB_LOOKUP(
    "Profile Supported Functions", 
    "Settings - Contract", 
    "[RecordID] = 1"
  ), "Approvals"
) > 0;

LET $approvalStepRequired :=
AND(
  $approvalsFeatureEnabled = TRUE, 
  $approvalRequired
); 

