  TRUE
)

Column.ValidationFormula:
IF(
  	AND(
	  	[Setup Complete] = TRUE,
	 	OR(
		  //validation when clicking on Unlock button
		  AND(
			[Temp] = NULL(),
			[Initial Contract Value Incl. Tax] = [Total Contract Value]
			),
		  //validation when clicking on Confirm button
		  AND(
			[Temp] = "Unlocked",
			loaded.[Initial Contract Value Excl. Tax] = loaded.[Total Contract Value Excl Tax]
			)
		 )
	 ),
  	TRUE,
  	"Contract value cannot be changed as this contract has approved extensions and/or variations"
)

Column.Formula:
LET $blockVisible := 
AND(
  OR(
	[Navigation] = "Overview",
	[Navigation] = "Summary"
  ),
  [Status] <> "Setup"
);

LET $columns := ARRAY("RecordID", "Status", "New User in Role", "New Person in Role", "DateCreated", "Role");

LET $data := 
IF( 
  AND(
    [Change of Role Data] = NULL, 
	$blockVisible = TRUE
  ), 

 IFERROR(
   DB_LOOKUPALL(
	$columns, 
	"Change of Role", 
    "[LineNumber] FAST_EQUAL_TO 1",
    HASH(
    	"parent", ARRAY(EFormType, RecordID),
    	"sort", "[DateCreated] DESC"
    )	
   ), 
   NULL()
 )
);

LET $hashed :=  
ARRAY_SELECT(
  $data, $(i, item)
  INDEXES_TO_KEYS(
    $item, 
    $columns
  )
);

LET $result := 
ARRAY_TO_HASH_GROUPED(
  $hashed, 
  $(i, item) HASH_GET($item, "Role"),
  $(i, item) $item
);

RETURN
IF( 
  AND(
	[Change of Role Data] = NULL
  ), 
  $result, 
  [Change of Role Data]
)

LET $profileSupportedFunctions := HASH_GET([Contract Module Settings], "Profile Supported Functions");
LET $blocksSupportedForContractType := HASH_GET([Contract Type Settings], "Enabled Blocks"); 

LET $blockEnabled := $(target, profileCheck, onlyShowOnSubContract)
(
  LET $isFunctionSupported := 
  AND(
    //Is this turned on against the contract type
    ARRAY_INDEXOF($blocksSupportedForContractType, $target) > 0,

    //Is this supported with the currnet contract profile
    IF( 
    $profileCheck = TRUE,  
      ARRAY_INDEXOF($profileSupportedFunctions, $target) > 0, 
    TRUE
    ),

    //Is this only shown on a subcontract / standalone contract
    IF(
      $onlyShowOnSubContract = TRUE, 
          [Type] <> "Head Agreement", 
      TRUE
    )
  );

  RETURN 
  IF($isFunctionSupported = TRUE, $target, NULL)
); 

LET $set := 
//This forces an order to how the processes output 
//and is a way  to force in additional processes in 
//specfic positions
ARRAY(
  CALL($blockEnabled, "Duration Summary"),  
  CALL($blockEnabled, "Simple Value Summary"),  
  CALL($blockEnabled, "Value Summary"),  
  CALL($blockEnabled, "Roles Summary"),
  CALL($blockEnabled, "Variations", TRUE),
  "Documentation",
  CALL($blockEnabled, "Procurement Details"),
  CALL($blockEnabled, "Contract Events", FALSE, TRUE),
  CALL($blockEnabled, "Related Records")
);

RETURN ARRAY_WHERE($set, $(i, item) $item <> NULL)

