Column.Formula:
//Tab / register map
LET $tabRegisterMap := HASH_GET([Contract Module Settings], "Contract Function Map");

//Enabled
////Contract Profile e.g. Lite, ACM
LET $profileFunctions := HASH_GET([Contract Module Settings], "Profile Supported Functions");
////Contract Settings
LET $systemEnabledHash := HASH_GET([Contract Module Settings], "Contract Function Hash");
////Contract Type
LET $enabledForContractType := HASH_GET([Contract Type Settings], "Enabled Registers");

LET $inThisContractTab :=
ARRAY_WHERE(
  HASH_GET($tabRegisterMap, [Navigation]), $(i, register) 
  IF(	
	AND(
      [Register Selection Mode] = "Manual",
	  ARRAY_INDEXOF($profileFunctions, "Manage Registers") > 0
	), 
	AND(
        ARRAY_INDEXOF($profileFunctions, $register) > 0,
        ARRAY_INDEXOF([Register Override], $register) > 0, 
		HASH_GET($systemEnabledHash, $register) = TRUE
		//Manual mode bypasses the contract type check
	  ),

    AND(
	  ARRAY_INDEXOF($enabledForContractType, $register) > 0,
	  HASH_GET($systemEnabledHash, $register) = TRUE, 
	  ARRAY_INDEXOF($profileFunctions, $register) > 0
	)
  )
);

RETURN 
IF(
  [Navigation] = "Subcontracts", 
    ARRAY("Subcontracts"),
  $inThisContractTab
)

