LET $calc := 
AND(
  [Contract Type] <> NULL, 
  [Contract Type Settings] = NULL
);

LET $data := 
IF( 
  $calc == TRUE, 
  DB_LOOKUP(
    $columns, 
    "Contract Type",
    "[RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT([Contract Type RecordID])
  )
);

RETURN 
IF( 
  $calc == TRUE, 
    INDEXES_TO_KEYS($data, $columns),
  [Contract Type Settings]
)

