Binding.Expression:
LET $enabledRegisters := HASH_GET([Contract Module Settings], "Profile Supported Functions"); 

LET $forms := HASH_KEYS([Contract Event Hash]); 

LET $formToFunctionMap := 
HASH(
  "Action", "Actions", 
  "Communication Event", "Communication", 
  "Contract Review", "Contract Review", 
  "Defects Liability", "Defects Liability",
  "Defects Entry", "Defects Entry", 
  "KPI Performance", "Key Performance Indicators",
  "KPI Target", "Key Performance Indicators", 
  "Meeting", "Meetings", 
  "Milestone", "Milestones", 
  "Novation", "Novation", 
  "Payment Certificate", "Payment Certificates", 
  "Penalty Clause", "Penalty Clauses", 
  "Security", "Security", 
  "Service Level Agreement", "Service Level Agreements", 
  "Transition", "Transition",
  "Warranty", "Warranties", 
  "Extension", "Extensions", 
  "Variation", "Variations"  
); 

LET $forms := 
ARRAY_WHERE(
  $forms, $(i, form)
  LET $conversion := HASH_GET($formToFunctionMap, $form); 
  RETURN ARRAY_INDEXOF($enabledRegisters, $conversion) > 0
); 

LET $data := 
ARRAY_SELECT(
  $forms, 
  $(i, form) 
  ARRAY($form, HASH_GET([Contract Event Hash], $form))
);

LET $transformed := 
ARRAY_SELECT(
  $data, $(i, item)
  LET $form := ARRAY_INDEX($item, 1); 
  LET $dates := ARRAY_JOIN(ARRAY_INDEX($item, 2), ", ");
  RETURN 
  $form & ": " & $dates
);

LET $result := ARRAY_JOIN($transformed, NEWLINE());

RETURN $result

LET $rows:= ARRAY_SELECT(
	[Contract Events Data],
	$(i, item) (
		LET $href := EBMS_GET_EFORMRECORD_URL(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2));
		LET $event := ARRAY_INDEX($item, 3);
		LET $date := ARRAY_INDEX($item, 4);

		LET $colour := 
	  IF(
			$event = "Contract End Date", "white", 
			$event = "Contract Start Date", "white",
			$event = "Date Approved", "#0a7c00", 
			$event = "Date Submitted", "#0700a3", 
			$date < NOW(), "#666666",
			$date < DATEADD("day", 7, NOW()), "#FFFFFF",
			"#000000"
		);

		LET $backcolour := 
		IF(
			$event = "Contract End Date", "#8f0000", 
			$event = "Contract Start Date", "#098f00",
			$date < NOW(), "#EEEEEE", 
			$date < DATEADD("day", 7, NOW()), "#00557A", 
			"#FFFFFF"
		);	

		LET $context := ARRAY_INDEX($item, 1) & " (ID:" & ARRAY_INDEX($item, 2) & ")";
		LET $eventText := ARRAY_INDEX($item, 3);

		RETURN 
		ARRAY(
			HASH("value", $context, "url", $href, "colour", $colour, "backcolour", $backcolour),
		//	HASH("value", ARRAY_INDEX($item, 2), "url", $href, "colour", $colour, "backcolour", $backcolour),
			HASH("value", $eventText, "url", $href, "colour", $colour, "backcolour", $backcolour),
			HASH("value", DATEFORMAT($date, "dd-MMM-yy"), "url", $href, "colour", $colour, "backcolour", $backcolour)
		)
	)
);


RETURN HASH(
	"columns", ARRAY(
		HASH(
			"heading","Context", 
			"width", 200
		), 
		//HASH(
		//	"heading","RecordID", 
		//	"width", 120
		//), 
		HASH(
			"heading","Event", 
			"width", 420
		), 
		HASH(
			"heading","Date", 
			"width", 120
		)
	), 
	"rows", $rows
)

