Navigate.UrlTemplate:
{
LET $cols:= 
ARRAY(
  "EFormType A",
  "RecordID A",
  "EFormType B",
  "RecordID B"
  );
LET $values:= 
	DB_LOOKUP(
		$cols,
		ARRAY_INDEX($identifier, 1),
	  HASH("RecordID", ARRAY_INDEX($identifier, 2) ),
	  HASH("credentials", [Credential])
	);

LET $hash:= INDEXES_TO_KEYS($values, $cols);

RETURN "/records/"&
IF(
	AND( 
	  	HASH_GET($hash, "EFormType A") = [EFormType], 
	  	HASH_GET($hash, "RecordID A") = [RecordID] 
  	),
  	HASH_GET($hash, "EFormType B") & "/" & HASH_GET($hash, "RecordID B"),
	HASH_GET($hash, "EFormType A") & "/" & HASH_GET($hash, "RecordID A")
)
}

