FieldEntry.Formula:
LET $thisrecord := DB_ENCODECONSTANT([RecordID]);
LET $thisform := DB_ENCODECONSTANT([EFormType]);
LET $existinglinks :=
DB_LOOKUPALL(
  ARRAY("EFormType A", "RecordID A", "EFormType B", "RecordID B"),
  "Record Relation",
  "[LineNumber] FAST_EQUAL_TO 1 AND [Status] FAST_EQUAL_TO 'Active' AND ([EFormType A] FAST_EQUAL_TO "& $thisform &" AND [RecordID A] FAST_EQUAL_TO "& $thisrecord &") OR ([EFormType B] FAST_EQUAL_TO "& $thisform &" AND [RecordID B] FAST_EQUAL_TO "& $thisrecord &")"
  );

LET $linkedRecords:=
ARRAY_WHERE(
	ARRAY_SELECTMANY(
		$existinglinks, $(i,item) 
		ARRAY(
		  ARRAY_TAKE($item,2),
		  ARRAY_SKIP($item, 2)
		  )
	  ),
	  $(j, jitem) $jitem <> ARRAY([EFormType], [RecordID])
);

LET $linkedRecords:=
ARRAY_SELECTMANY(
	$linkedRecords, $(i,item) 
	IF( ARRAY_FIRST($item) = [Link EFormType], ARRAY_INDEX($item, 2), NULL)
);
LET $filter := 
IF( 
  AND(
    $linkedRecords <> NULL, 
    $linkedRecords <> ARRAY() 
  ), 
  " AND ([RecordID] <> " & 
	ARRAY_JOIN($linkedRecords, " AND [RecordID] <> ") &
	")"
);

LET $set := 
DB_LOOKUPALL(
  ARRAY(
	"RecordID",
    "Description"
  ), 
  [Link EFormType],
  "[Description] IS NOT EMPTY AND [LineNumber] FAST_EQUAL_TO 1" & 
  IF(
	[Link EFormType] = [EFormType], 
	" AND [RecordID] <> " & DB_ENCODECONSTANT([RecordID])
  )& $filter
);


RETURN $set

Binding.Expression:
LET $set := 
ARRAY_SELECT(
  [Linkable Records], $(i, item)
  LET $recordID := ARRAY_FIRST($item); 
  LET $description := ARRAY_LAST($item); 
  RETURN
  HASH(
    "value", $recordID,  
    "text", $description
  )
);

RETURN 
COALESCE($set, ARRAY())

LET $extensionValue := HASH_GET([Extension Data], "Total Exercised Extension Value excl Tax");
LET $variationValue := [Approved Variation Value];

RETURN NUMBERFORMAT($extensionValue + $variationValue, "C")

LET $extensionValue := HASH_GET([Extension Data], "Total Exercised Extension Value incl Tax");
LET $variationValue := [Approved Variation Value] + HASH_GET([Variation Data], "Total Approved Tax") + [Imported Variation Tax];

RETURN NUMBERFORMAT($extensionValue + $variationValue, "C")

