BulkCreateRecords.FieldValuesFormula:
//get data from Action Set
LET $setData := 
 ARRAY_SORT(
   DB_LOOKUPALL(
	  ARRAY(
		"Action RecordID",
		"Action Sequence"
	  ),
	  "_ActionSet", 
	  "[RecordID] = " & DB_ENCODECONSTANT([Action Set])
 	)
);

//get details from Action form
LET $columns := 
	ARRAY(
		"RecordID",
		"Action",
		"Details"
	);

LET $actionDetails :=
	 DB_LOOKUPALL(
	  	$columns, 
		"Action",
	  	"[RecordID] IN " & DB_ENCODECONSTANT(ARRAY_FIRST(ARRAY_TRANSPOSE($setData)))
	);

//combine the details from Action form with the sequence in Action Set
LET $actionData := 
	ARRAY_SORT(
		ARRAY_SELECT(
		$setData, $(i, item) 
		LET $actionSequence := ARRAY_LAST($item);
		LET $data :=
			ARRAY_FIRST(
				ARRAY_WHERE(
				$actionDetails, $(j , jtem)
				ARRAY_FIRST($item) = ARRAY_FIRST($jtem)
				)
			);
		RETURN ARRAY_CONCAT($actionSequence, $data)
		)
	);

//format into array of hashes
LET $content := ARRAY_SELECT($actionData, $(a, action) 
  				INDEXES_TO_KEYS(
				  	ARRAY_CONCAT(ARRAY_SKIP($action, 1), "Details", TRUE),
				  	ARRAY_CONCAT($columns, "Navigation", "Created From Template")
  				)
		);

RETURN 
ARRAY_SELECT(
	$content, $(c, content)
  	HASH(
	  	"content", $content,
	  	"parent", ARRAY([EFormType], [RecordID])
	)
)

Binding.Expression:
LET $map := 
HASH(
	"Work Orders", 			"Work Order", 
	"Payment Certificates",	"Payment Certificate"
);

LET $filtered :=
ARRAY_WHERE(
  	IF(
	  	[Register Selection Mode] = "Manual", [Register Override],
		HASH_GET([Contract Type Settings], "Enabled Registers")
	), $(i, item) 
	OR(	
		$item = "Payment Certificates", 
		$item = "Work Orders"
	)
);

RETURN 
ARRAY_CONCAT(
  ARRAY("Variation", "Extension"),
  ARRAY_SELECT(
	$filtered, $(i, item) 
	HASH_GET($map, $item)
))

LET $enabledRegisters := HASH_GET([Contract Module Settings], "Profile Supported Functions"); 

LET $forms := HASH_KEYS([Contract Event Hash]); 

LET $formToFunctionMap := 
HASH(
  "Action", "Actions", 
  "Communication Event", "Communication", 
  "Contract Review", "Contract Review", 
  "Defects Liability", "Defects Liability",
  "Defects Entry", "Defects Entry", 
  "KPI Performance", "Key Performance Indicators",
  "KPI Target", "Key Performance Indicators", 
  "Meeting", "Meetings", 
  "Milestone", "Milestones", 
  "Novation", "Novation", 
  "Payment Certificate", "Payment Certificates", 
  "Penalty Clause", "Penalty Clauses", 
  "Security", "Security", 
  "Service Level Agreement", "Service Level Agreements", 
  "Transition", "Transition",
  "Warranty", "Warranties", 
  "Extension", "Extensions", 
  "Variation", "Variations"  
); 

LET $forms := 
ARRAY_WHERE(
  $forms, $(i, form)
  LET $conversion := HASH_GET($formToFunctionMap, $form); 
  RETURN ARRAY_INDEXOF($enabledRegisters, $conversion) > 0
); 

LET $data := 
ARRAY_SELECT(
  $forms, 
  $(i, form) 
  ARRAY($form, HASH_GET([Contract Event Hash], $form))
);

LET $transformed := 
ARRAY_SELECT(
  $data, $(i, item)
  LET $form := ARRAY_INDEX($item, 1); 
  LET $dates := ARRAY_JOIN(ARRAY_INDEX($item, 2), ", ");
  RETURN 
  $form & ": " & $dates
);

LET $result := ARRAY_JOIN($transformed, NEWLINE());

RETURN $result

