FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns:= ARRAY( "Potential Vendor RecordID", "Potential Vendor Name", "Potential Vendor Description", "Potential Vendor BusinessNumber Type", "Potential Vendor BusinessNumber", "Potential Vendor ID", "Subcontract Start Date", "Subcontract End Date", "Subcontract Value" ); LET $existing := ARRAY_TRANSPOSE( ARRAY_SELECT( $columns, $(c, col) CURRENT_EACHVALUE($col) ) );
LET $newSet := ARRAY( [Temp Vendor RecordID], [Temp Vendor Name], [Temp Vendor Description], [Temp Vendor BusinessNumber Type], [Temp Vendor BusinessNumber], [Temp Vendor ID], [Temp Start Date], [Temp End Date], [Temp Value] );
LET $combined := ARRAY_CONCAT( $existing, ARRAY($newSet) );
LET $filtered := ARRAY_WHERE( $combined, $(i, item) ARRAY_INDEX($item, 1) <> NULL() );
LET $result := ARRAY_DISTINCT($filtered);
RETURN INDEXES_TO_KEYS( ARRAY_TRANSPOSE($result), $columns )

