TriggerCustomEvent.EventArgumentsFormula:
LET $targetType := "Contract Initiation"; 

LET $data := HASH_GET([Submission Data], "Raw");

LET $matches := 
ARRAY_SELECT(ARRAY_WHERE(
  $data, $(i, item) 
  HASH_GET($item, "Type") = $targetType
), $(i, item) HASH_GET($item, "RecordID"));  

RETURN HASH("RecordID", GREATEST($matches))

LET $targetType := "Contract Initiation";
LET $mostRecentSubmission := DB_LOOKUP( ARRAY("RecordID", "Round"), "Submission", "[Type] = " & DB_ENCODECONSTANT($targetType), HASH("sort", "[Round] DESC", "ancestor", ARRAY(EFormType, RecordID)) );
RETURN HASH( "Submission Type", $targetType, "Previous Submission Round", COALESCE(ARRAY_LAST($mostRecentSubmission), 0), "Previous Submission RecordID", ARRAY_FIRST($mostRecentSubmission), "Parent Record", ARRAY([EFormType], [RecordID]), "Review Record", ARRAY([EFormType], [RecordID]), "Submitter Notes", NULL )

Binding.Expression:
LET $category := "Approval";
LET $targetType := "Contract Initiation";
LET $targetStatus := "Draft";

LET $data := HASH_GET([Submission Data], "Raw");

LET $matches := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
	HASH_GET($item, "Type") = $targetType,
    HASH_GET($item, "Status") = $targetStatus,
  )
);  

LET $submissions := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
	HASH_GET($item, "Type") = $targetType
  )
);   

LET $numberInDraft := ARRAY_LENGTH($matches); 
LET $numberOfSubmission := ARRAY_LENGTH($submissions); 

LET $approvalRequired := 
(
  LET $type := $targetType; 
  LET $workflowSettings := HASH_GET([Approval Workflow Settings], $type);
  RETURN NOT(HASH_GET($workflowSettings, "Not Required"))
);
RETURN
AND(
  	[Current Question] = $category,
  	[Wizard Status] = "In Progress", 
  	ARRAY_INDEXOF([Wizard Questions], $category) > 0,
  	[Approval Not Required] <> TRUE,
  	OR(
		$numberInDraft > 0,
		$numberOfSubmission = 0
	)
)

