Binding.Expression:
LET $category := "Approval";
LET $targetType := "Contract Initiation";

LET $data := HASH_GET([Submission Data], "Raw");

LET $inProgress := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
	HASH_GET($item, "Type") = $targetType,
	OR(
      HASH_GET($item, "Status") = "Draft",
      HASH_GET($item, "Status") = "Submitted",
      HASH_GET($item, "Status") = "Approved"
	)	
  )
);   
LET $rejected := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
    HASH_GET($item, "Type") = $targetType,
	OR(
	  HASH_GET($item, "Status") = "Voided",
	  HASH_GET($item, "Status") = "Rejected",
	  HASH_GET($item, "Status") = "Cancelled"
	)
  )
); 

LET $numberInProgress := ARRAY_LENGTH($inProgress); 
LET $numberRejected := ARRAY_LENGTH($rejected);

RETURN
AND(
  	[Current Question] = $category,
  	[Wizard Status] = "In Progress", 
  	ARRAY_INDEXOF([Wizard Questions], $category) > 0,
  	[Approval Not Required] <> TRUE,
	$numberRejected > 0,
	$numberInProgress = 0
)

