LET $html :=  
ARRAY_SELECT(
  $matches, $(i, item) 

  LET $lastEventDate := HASH_GET($item, "Last Decision Date");
  LET $lastDecisionMaker := HASH_GET($item, "Last Decision Maker");
  LET $status := HASH_GET($item, "Status");
