FieldEntry.Formula:
LET $numberInRow := N($clickid); 

LET $processes := 
ARRAY(
  "Details", 
  "Duration Summary", 
  "Value Summary", 
  "Documentation" 
); 

LET $process := ARRAY_INDEX($processes, $numberInRow);

LET $result := 
IF(
  ARRAY_INDEXOF([Visible Steps], $process) > 0, 
  ARRAY_WHERE([Visible Steps], $(i, item) $item <> $process), 
  ARRAY_CONCAT([Visible Steps], $process)
);

RETURN 
IF(
  [Support Multiple Visible] = TRUE, 
  ARRAY_WHERE(
    ARRAY_DISTINCT($result), $(i, item) 
    $item <> NULL
  ), 

  IF( 
	$numberInRow <= 0, 
	ARRAY("Details"),
    ARRAY($process)
  )
)

