  LET $selected := ARRAY_INDEXOF([Visible Steps], $item) > 0; 
  RETURN
  "<div style='display: none;' data-tabname='" & $item & "' data-clickid='" & $i & "' data-state='selected' class='core-tabfile-item'>" & $processDisplayName & "</div>" & 
  "<div style='display: none;' data-tabname='" & $item & "' data-clickid='" & $i & "' data-state='notselected' class='core-tabfile-item'>" & $processDisplayName & "</div>"
);

RETURN "<div class='core-tabfile-bar'>" & ARRAY_JOIN($items, "") & "</div>"

