FieldEntry.Formula:
LET $years := DATEADD("year", [Duration Years], [Start Date]);
LET $months := DATEADD("month", [Duration Months], $years);
LET $days := DATEADD("day", [Duration Days], $months);

LET $days := 
IF( 
	$days > [Start Date], 
	DATEADD("day", -1, $days),
	$days
);

RETURN 
IF( 
  [Perpetual] = TRUE, 
    NULL, 

  ISBLANK([Start Date]),	
	[Original End Date],

  $days
)

LET $startDate := [Start Date];
LET $year := YEAR($startDate);
LET $month := MONTH($startDate);
RETURN DATE($year, $month)

LET $hasExtensions := OR( HASH_GET([Extension Data], "Count of Submitted Extensions") > 0, HASH_GET([Extension Data], "Count of Exercised Extensions") > 0 );
RETURN
IF( $hasExtensions = TRUE, [Current End Date], [Original End Date] )

ARRAY_FIRST(
	ARRAY_FIRST(
		DB_REPORT(
			"COUNT([RecordID])", 
			"Payment Certificate", 
			"[LineNumber] FAST_EQUAL_TO 1",
		  HASH("parent", ARRAY("Contract", [RecordID]))
		)
	)
) + 1

