Binding.Expression:
LET $active := "background: #CBE8FF; color: #1E70B5;";
LET $inactive := "background: #EFEFEF; color: #404040;";
LET $expired := "background: #FCDBDF; color: #FF5D5D;";

LET $lockIcon :=
"<svg height='12' width='11' viewBox='0 0 448 512'><path fill='#FFA800' d='M144 144v48H304V144c0-44.2-35.8-80-80-80s-80 35.8-80 80zM80 192V144C80 64.5 144.5 0 224 0s144 64.5 144 144v48h16c35.3 0 64 28.7 64 64V448c0 35.3-28.7 64-64 64H64c-35.3 0-64-28.7-64-64V256c0-35.3 28.7-64 64-64H80z'/></svg>";
LET $restricted :=
IF(
  	[Restricted] == TRUE,
  	"<span style='padding: 3px 8px; margin: 5px; border-radius: 15px; vertical-align: 10px; font-weight: 600; background: #FFFBE7; color: #FFA800;'>Restricted " & $lockIcon & "</span>",
  	""
);

RETURN

"<div style='width: 100%; max-height:50px;"&IF(LEN([Contract Title]) > 150," overflow: auto;","")&"'>" &
"<span style='font-weight:bold; font-size: 2em; color:#000000; margin-right: 5px;'>" & HTMLENCODE([Contract Title]) & "</span>" &
"<span style='padding: 3px 8px; margin: 5px; border-radius: 15px; vertical-align: 10px; font-weight: 600; " &
IF(
	[Status] = "Active", $active,
	OR([Status] = "Expired", [Status] = "Terminated"), $expired,
	$inactive
) & "'>" & HTMLENCODE([Status]) & "</span>" &
$restricted &
"</div>"

FieldEntry.Formula:
LET $thisrecord := DB_ENCODECONSTANT([RecordID]);
LET $thisform := DB_ENCODECONSTANT([EFormType]);
LET $existinglinks :=
DB_LOOKUPALL(
  ARRAY("EFormType A", "RecordID A", "EFormType B", "RecordID B"),
  "Record Relation",
  "[LineNumber] FAST_EQUAL_TO 1 AND [Status] FAST_EQUAL_TO 'Active' AND ([EFormType A] FAST_EQUAL_TO "& $thisform &" AND [RecordID A] FAST_EQUAL_TO "& $thisrecord &") OR ([EFormType B] FAST_EQUAL_TO "& $thisform &" AND [RecordID B] FAST_EQUAL_TO "& $thisrecord &")"
  );

LET $linkedRecords:=
ARRAY_WHERE(
	ARRAY_SELECTMANY(
		$existinglinks, $(i,item) 
		ARRAY(
		  ARRAY_TAKE($item,2),
		  ARRAY_SKIP($item, 2)
		  )
	  ),
	  $(j, jitem) $jitem <> ARRAY([EFormType], [RecordID])
);

LET $linkedRecords:=
ARRAY_SELECTMANY(
	$linkedRecords, $(i,item) 
	IF( ARRAY_FIRST($item) = [Link EFormType], ARRAY_INDEX($item, 2), NULL)
);
LET $filter := 
IF( 
  AND(
    $linkedRecords <> NULL, 
    $linkedRecords <> ARRAY() 
  ), 
  " AND ([RecordID] <> " & 
	ARRAY_JOIN($linkedRecords, " AND [RecordID] <> ") &
	")"
);

LET $set := 
DB_LOOKUPALL(
  ARRAY(
	"RecordID",
    "Description"
  ), 
  [Link EFormType],
  "[Description] IS NOT EMPTY AND [LineNumber] FAST_EQUAL_TO 1" & 
  IF(
	[Link EFormType] = [EFormType], 
	" AND [RecordID] <> " & DB_ENCODECONSTANT([RecordID])
  )& $filter
);


RETURN $set

DisplayMessage.MessageTemplate:
{ LET $action:= HASH_GET($eventargs, "Action");
RETURN IF( 
  	$action = "Remove", "Link Removed", 
  	$action = "Add", "Link Added", NULL
  )}

FieldEntry.Formula:
LET $totalReceived := 
	COALESCE(
	  ARRAY_FIRST(
		ARRAY_FIRST(
			DB_REPORT(
				"SUM([Total Received])", 
				"Purchase Order",
				"[LineNumber] = 1 AND [Status] = 'Approved' OR [Status] = 'Completed'",
				HASH("ancestor", ARRAY(EFormType, RecordID))
			)
		)
	),
	0
);


RETURN $totalReceived

LET $data := 
DB_REPORT(
  "SUM([Funding Amount])", 
  "Funding", 
  HASH(
    "Funding Source RecordID", [RecordID],
    "Funding Source EFormType", [EFormType]
  )
);

RETURN COALESCE(ARRAY_FIRST(ARRAY_FIRST($data)), 0)

//$eventargs = ARRAY(businessnumbertype, businessnumber)
LET $businessNumberType := COALESCE(ARRAY_INDEX($eventargs, 1), [New Business Number Type]);
LET $businessNumber := COALESCE(ARRAY_INDEX($eventargs, 2), [New Vendor BusinessNumber]);

LET $format := 
DB_LOOKUP(
  "Business Number Format", 
  "Settings - Vendor", 
  "[RecordID] = 1 AND [Business Number Type] = " & DB_ENCODECONSTANT($businessNumberType)
);

LET $formatLength := LEN(REPLACESUBSTRING(" ", $format, ""));

LET $data := $businessNumber;
LET $isNumber := ISERROR(N(REPLACESUBSTRING(" ", $businessNumber, ""))) <> TRUE;
LET $filtered := ARRAY_JOIN(SPLIT($data, " "), "");
RETURN
IF( 
  $format = NULL, 
    $data,
  $isNumber,
    NUMBERFORMAT(LEFT($filtered, $formatLength), $format),
  $data
)

