FieldEntry.Formula:
LET $columns := ARRAY( "Question", "Answer", "Comments" );
LET $data := DB_LOOKUPALL( $columns, "Questionnaire", HASH("RecordID", [Supplementary Data RecordID]) );
RETURN ARRAY_SELECT( $data, $(i, item) INDEXES_TO_KEYS($item, $columns) )

LET $functionMap := HASH_GET([Contract Module Settings], "Contract Function Map"); 
//LET $contractTypeRegisters := HASH_GET([Contract Type Settings], "Enabled Registers");
LET $profileFunctions := HASH_GET([Contract Module Settings], "Profile Supported Functions");
LET $systemEnabledRegisters := HASH_GET([Contract Module Settings], "Contract Function Hash");

LET $allFunctions := 
ARRAY_SELECTMANY(
  HASH_KEYS($functionMap), $(f, function) 
  ARRAY_SORT(HASH_GET($functionMap, $function), $(item) $item)
); 

LET $allowedForContractProfile :=
ARRAY_WHERE(
  $allFunctions, $(i, register) 
  ARRAY_INDEXOF($profileFunctions, $register) > 0
);

LET $allowedForSystem :=
ARRAY_WHERE(
  $allowedForContractProfile, $(i, register) 
  HASH_GET($systemEnabledRegisters, $register) = TRUE
);

//LET $allowedForContractType := 
//ARRAY_WHERE(
//  $allFunctions, $(i, item) 
//  ARRAY_INDEXOF($contractTypeRegisters, $item) > 0
//); 

RETURN 
ARRAY_SELECT(
  $allowedForSystem, $(i, item) 
  ARRAY(
	$item, 
	TRUE
  )
)

LET $functionMap := HASH_GET([Contract Module Settings], "Contract Function Map"); 
//LET $contractTypeRegisters := HASH_GET([Contract Type Settings], "Enabled Registers");
LET $profileFunctions := HASH_GET([Contract Module Settings], "Profile Supported Functions");
LET $systemEnabledRegisters := HASH_GET([Contract Module Settings], "Contract Function Hash");

LET $allFunctions := 
ARRAY_SELECTMANY(
  HASH_KEYS($functionMap), $(f, function) 
  ARRAY_SORT(HASH_GET($functionMap, $function), $(item) $item)
); 

LET $allowedForContractProfile :=
ARRAY_WHERE(
  $allFunctions, $(i, register) 
  ARRAY_INDEXOF($profileFunctions, $register) > 0
);

LET $allowedForSystem :=
ARRAY_WHERE(
  $allowedForContractProfile, $(i, register) 
  HASH_GET($systemEnabledRegisters, $register) = TRUE
);

//LET $allowedForContractType := 
//ARRAY_WHERE(
//  $allFunctions, $(i, item) 
//  ARRAY_INDEXOF($contractTypeRegisters, $item) > 0
//); 

RETURN 
ARRAY_SELECT(
  $allowedForSystem, $(i, item) 
  ARRAY(
	$item, 
	TRUE
  )
)

LET $allActions :=
DB_LOOKUPALL(
	"Vendor Action", 
	"Settings - Contract", 
	"[Vendor Action Enabled] = TRUE" & 
	" AND [Vendor Action] <> 'Contacts'" &
	" AND [Vendor Action] <> 'Insurances'"
);

LET $existingActions := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Vendor Action],
		EACH [Vendor Action Enabled]
	)
);

LET $newActions := 
ARRAY_SELECT(
	ARRAY_WHERE(
		$allActions, $(a, action)
		ARRAY_INDEXOF(
			ARRAY_INDEX(ARRAY_TRANSPOSE($existingActions), 1), 
			$action
		) = NULL()
	), 
	$(a, action)
	ARRAY($action, FALSE)
);

LET $actionsStaying := 
ARRAY_WHERE(
	$existingActions, $(a, action)
	ARRAY_INDEXOF(
		$allActions, 
		ARRAY_INDEX($action, 1)
	) > NULL()
);

LET $updatedActionsList :=
ARRAY_CONCAT(
	$actionsStaying,
	$newActions
);

RETURN $updatedActionsList

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $multicolumns:=
ARRAY(
  	//roles
  	"Role",
  	"Username in Role",
  	"Custom Role",
  	"Role is not a System User",
  	"Person in Role"
);
LET $multivalues:=
DB_LOOKUPALL(
  $multicolumns, "Contract",
  "[Role] IS NOT EMPTY AND [RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT([Head Agreement RecordID])  
  );

LET $columns:=
ARRAY(
  	"Contract Number",
	"Contract Type",
	"Contract Title",
	"Contract Summary",
	"Department",
  	"Contract Category",
  	"Contract Subcategory"
);

LET $values:= 
DB_LOOKUP(
  $columns, "Contract",
  "[LineNumber] FAST_EQUAL_TO 1 AND [RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT([Head Agreement RecordID])  
  );

LET $allvalues:= 
ARRAY_CONCAT(
	ARRAY_SELECT(
		ARRAY_SKIP($values,1) , $(v, val) ARRAY($val)
	),
	ARRAY_TRANSPOSE($multivalues)
	);

LET $final:=
INDEXES_TO_KEYS(
  $allvalues,
 ARRAY_CONCAT( ARRAY_SKIP($columns, 1), $multicolumns)
  );

LET $subNumber:=
ARRAY_FIRST(
	ARRAY_FIRST(
		DB_REPORT(
			"MAX([Subcontract Number])", 
			"Contract", 
			"[LineNumber] FAST_EQUAL_TO 1 AND [RecordID] <> "& DB_ENCODECONSTANT([RecordID])  ,
		  HASH("parent", ARRAY("Contract", [Head Agreement RecordID]))
		)
	)
) + 1;

RETURN
HASH_MERGE(
  HASH(
	"Subcontract Number", ARRAY($subNumber),
	"Contract Number", ARRAY(ARRAY_INDEX($values, 1) & "-" &$subNumber)
  ),
  $final
  )

FieldEntry.Formula:
IF( 
	AND(
		[Setup Complete] = TRUE, 
		[Navigation] = "Overview"
	),
(
	LET $customEvents := 
ARRAY(
	ARRAY(EFormType, RecordID, "Contract Start Date", [Start Date]),
	ARRAY(EFormType, RecordID, "Contract End Date", [Current End Date]),
	IF([Status]=="Contract Closed", ARRAY(EFormType, RecordID, "Contract Closed", [Date Contract Closed])),
	IF([Status]=="Cancelled", ARRAY(EFormType, RecordID, "Contract Cancelled", [Date Contract Cancelled]))
);

LET $customEvents := 
ARRAY_WHERE(
	$customEvents, $(e, event) 
	$event <> NULL()
);

LET $fieldMappings := [Contract Event Hash];

LET $childForms := HASH_KEYS($fieldMappings);

LET $childData := 
ARRAY_SELECTMANY(
	$childForms, $(c, childEFormType)
	(
		LET $targetFields := HASH_GET($fieldMappings, $childEFormType);

		LET $fieldData := 
		ARRAY_SELECTMANY(
			$targetFields, $(f, field)

			LET $result := 
			DB_LOOKUPALL(
				ARRAY("EFormType", "RecordID", $field),
				$childEFormType,
				"[LineNumber] FAST_EQUAL_TO 1 AND [Contract RecordID] = " & DB_ENCODECONSTANT([RecordID])
			);

			LET $cleaned := 
			ARRAY_WHERE(
				$result, $(i, item) 
				ARRAY_INDEX($item, 3) <> NULL()
			);

			LET $injected := 
			ARRAY_SELECT(
				$cleaned, $(i, item)
				ARRAY_CONCAT(
					ARRAY_TAKE($item, 2), 
					$field, 
					ARRAY_INDEX($item, 3)
				)
			);

			RETURN $injected
		);

		LET $cleaned := 
		ARRAY_WHERE(
			$fieldData, $(i, item) 
			ARRAY_LENGTH($item) > 0
		);

		RETURN $cleaned
	)
);

LET $combined := 
ARRAY_CONCAT(
	$customEvents, 
	$childData
); 

LET $sorted := ARRAY_SORT($combined, $(item) ARRAY_INDEX($item, 4));

RETURN $sorted
)

)

LET $systemRole := ROLE("System");

LET $extensionData := 
ARRAY_SELECT(
	HASH_GET([Extension Data], "Notes Data"), $(i, item)
  	ARRAY(
  		$systemRole,
  		ARRAY_INDEX($item, 2),
  		"Extension ID: " & ARRAY_INDEX($item, 1) & " Exercised"
	)
);

LET $variationData :=
ARRAY_SELECT(
	HASH_GET([Variation Data], "Notes Data"), $(i, item)
  	ARRAY(
  		$systemRole,
  		ARRAY_INDEX($item, 2),
  		"Variation ID: " & ARRAY_INDEX($item, 1) & " Approved"
	)
);

LET $collectedData :=
ARRAY_CONCAT(
	[Notes],
  	$extensionData,
  	$variationData
);

LET $dateSortedData := 
ARRAY_SORT(
  	$collectedData, $(item)
  	ARRAY_INDEX($item, 2) //sort by datetime asc i.e. Oldest -> Newest
);

LET $cleanData := 
ARRAY_WHERE(
	$dateSortedData, $(i, item) 
	ARRAY_INDEX($item, 3) <> NULL()
);

LET $formatted := 
ARRAY_SELECT(
	$cleanData, $(i, item) 
	LET $user := ARRAY_INDEX($item, 1); 
	LET $datePosted := ARRAY_INDEX($item, 2); 
	LET $message := IFERROR(ARRAY_INDEX($item, 3), "");

	LET $isSystem := $systemRole == $user;
	LET $isCurrentUser := ARRAY_FIRST([User to Group Mappings]) = $user;
	LET $userDisplayName := IF($isSystem = TRUE, "System", $isCurrentUser = TRUE, [Current User Display Name], EBMS_GET_ROLE_DISPLAYNAME($user)); 
	LET $datePostedFormatted := DATEFORMAT($datePosted, "dd-MMM-yyyy hh:mm tt");
	LET $class := IF($isSystem, "system", $isCurrentUser = TRUE, "right-top", "left-top");
	LET $colour := IF($isSystem, "#000000", $isCurrentUser = TRUE, "#00497b" , "#347016");

    LET $systemMessage := 
    "<div class=""systemChatDisplay " & $class & """>" &     	
	"<span class=""nimblexCOREChatMessageContainer"">" & $message & "</span>" & 
  	"<span style=""float:right;""> " & $datePostedFormatted & "</span>" &
	"</div>";

    LET $chatMessage := 
	"<div class=""talk-bubble tri-right " & $class & """>" &   
	"<span style=""font-weight: 600; color: " & $colour & """>" & HTMLENCODE($userDisplayName) & "</span>" & 
	"<span style=""font-style: italic;""> " & $datePostedFormatted & "</span>:" & 
	"<span class=""nimblexCOREChatMessageContainer"" style=""text-align: left; word-wrap: break-word;"">" & $message & "</span>" & 
	"</div>";

	RETURN 
	IF(
	  	$isSystem,
	  		$systemMessage,
	  	$chatMessage
  	)
);

RETURN 
IFERROR(
	ARRAY_JOIN(
		$formatted, ""
	) & "<a id='ScrollToDiscussionEndAnchor'></a>", 

	NULL()
)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $new:= ARRAY("Contract Manager", "Contract Owner", "Contract Sponsor");

LET $newMap:= 
INDEXES_TO_KEYS(
	ARRAY([Contract Manager Import], [Contract Owner Import], [Contract Sponsor Import]),
	$new
);
LET $roles:=
ARRAY_DISTINCT(
	ARRAY_CONCAT(
		EACH [Role], //existing
		$new // new
		)
	);

LET $existingData := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Role], 
		EACH [Username in Role],  //1
		EACH [Custom Role],
		EACH [Person in Role], 
		EACH [Change of Role Status],
		EACH [Role is not a System User] //5
	)
);

LET $existingMap := 
ARRAY_TO_HASH(
	$existingData, 
	$(i,item) ARRAY_INDEX($item, 1),
	$(i,item) ARRAY_SKIP($item, 1)
);

LET $importData:= 
ARRAY_SELECT(
	$roles, $(r, role)

	LET $isNew:= ARRAY_INDEXOF($new, $role)>0;

	//sets -------array of: Username in role, custom role, person in role, change of role status, role is not system user
	LET $newSet:= ARRAY( HASH_GET($newMap, $role), FALSE, NULL, NULL, FALSE);
	LET $existingSet:= HASH_GET($existingMap, $role); // ARRAY (5)
	RETURN
	ARRAY_CONCAT(
		$role,
		IF(  $isNew = TRUE, $newSet, $existingSet)
	)
);

RETURN 
INDEXES_TO_KEYS(
  	//filters data for any unassigned roles, then return a HASH(Field Name, ARRAY(Data to Populate)...)
	ARRAY_TRANSPOSE(ARRAY_WHERE($importData, $(i,item) ARRAY_INDEX($item, 2) <> NULL())),
	ARRAY(
		"Role",
		"Username in Role",
		"Custom Role",
		"Person in Role",
		"Change of Role Status",
		"Role is not a System User"
	)
)

FormulaDrivenRaiseLinkedEForm.NewRecordCountFormula:
ARRAY_LENGTH(
  	ARRAY_WHERE( 
	  EACH [Potential Vendor RecordID], $(i,item) 
	  AND( $item <> NULL, [Subcontract RecordID]#$i = NULL)
	)
)

FormulaDrivenRaiseLinkedEForm.FieldValuesFormula:
LET $columns:= ARRAY( "Potential Vendor RecordID", "Potential Vendor Name", "Potential Vendor BusinessNumber Type", "Potential Vendor BusinessNumber", "Potential Vendor ID", "Subcontract Start Date",//6 "Subcontract End Date",//7 "Subcontract Value", //8 "Subcontract Value", //9
"Contract Type RecordID", "Contract Title", "Contract Summary", "Department", "Contract Category", "Contract Subcategory",
//roles "Role", "Username in Role", "Custom Role", "Role is not a System User", "Person in Role" );
LET $values:= ARRAY_SELECT( $columns, $(c, col) CURRENT_EACHVALUE($col) );
LET $potential := EACH[Potential Vendor RecordID];
//filter out vendors with subcontract already created LET $filteredVendors:= ARRAY_WHERE( ARRAY_TRANSPOSE(ARRAY_TAKE($values,9)), $(i,item) AND($item <> NULL, [Subcontract RecordID]#$i = NULL) );
LET $values:= ARRAY_CONCAT( ARRAY_TRANSPOSE($filteredVendors), ARRAY_SKIP($values, 9) );
LET $output:= ARRAY_SELECT( ARRAY_INDEX($values,1), //set of vendor 1 $(i,item) ( LET $vendorData:= ARRAY_INDEX( ARRAY_TRANSPOSE( ARRAY_TAKE($values, 9)), $i); LET $procurementID:= IF([Respondent RecordID]#$i <> NULL, [Procurement RecordID], NULL); LET $duration:= ARRAY_TAKE( DATEDIFF_PARTS( DATEADD("day", 1, ARRAY_INDEX($vendorData,7)), //subcontract end date ARRAY_INDEX($vendorData,6) //subcontract start date ),3 );
LET $subcontractNumber := ARRAY_INDEXOF($potential, ARRAY_FIRST($vendorData));
LET $data:= ARRAY_CONCAT( ARRAY_SELECT($vendorData, $(i, item) ARRAY($item)), ARRAY_SELECT($duration, $(i,item) ARRAY($item)), ARRAY(ARRAY([Contract Number] & "-" & $subcontractNumber)), //contract number ARRAY(ARRAY($subcontractNumber)), //subcontract number ARRAY(ARRAY($procurementID)), //procurementID ARRAY_SKIP($values, 9) ); RETURN INDEXES_TO_KEYS( $data, ARRAY_CONCAT( ARRAY( "Vendor RecordID", "Vendor Name", "Business Number Type", "BusinessNumber", "Vendor ID", "Start Date", "Original End Date", "Initial Contract Value Excl. Tax", "Initial Contract Value Incl. Tax",
"Duration Years", "Duration Months", "Duration Days",
"Contract Number", "Subcontract Number", "Procurement RecordID" ), ARRAY_SKIP($columns, 9) ) ) ) ); RETURN HASH_MERGE( HASH( "Type", ARRAY("Subcontract"), "Subcontract Source", ARRAY("Contract") ), ARRAY_INDEX($output, $count) )

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $contracts:= [Temp];
LET $respondents:= 
	ARRAY_WHERE(
		EACH [Respondent RecordID], 
		$(i,item) $item <> NULL 
	);
RETURN
HASH(
  "Contract ID",
	ARRAY_SELECT(
		  $respondents, $(i,item) 
		  ARRAY_INDEX([Temp], $i)
	)
  )

LET $usertoReplace := HASH_GET($eventargs, "Previous Assignee");
LET $newUser := HASH_GET($eventargs, "New Assignee");

LET $assignees := EACH [Username in Role];

LET $reassignedRoles :=
ARRAY_SELECT(
	$assignees, $(i,item)
	IF($item = $usertoReplace, $newUser, $item)
);

LET $newData := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Role], 
		$reassignedRoles,  //1
		EACH [Custom Role],
		EACH [Person in Role], 
		EACH [Change of Role Status],
		EACH [Role is not a System User] //5
	)
);

RETURN 
INDEXES_TO_KEYS(
	ARRAY_TRANSPOSE(ARRAY_WHERE($newData,$(i,item) ARRAY_INDEX($item,2) <> NULL)),
	ARRAY(
		"Role",
		"Username in Role",
		"Custom Role",
		"Person in Role",
		"Change of Role Status",
		"Role is not a System User"
	)
)

FieldEntry.Formula:
LET $columns := 
ARRAY("RecordID", "Action", "Date Due");

LET $raw :=
DB_LOOKUPALL(
  $columns,
  "Action",
  "[LineNumber] = 1 AND [Status] <> 'Complete' AND [Status] <> 'Cancelled')",
  HASH("ancestor", ARRAY(EFormType, RecordID))
);

LET $result := ARRAY_SELECT($raw, $(i,item) INDEXES_TO_KEYS($item,$columns));

RETURN ARRAY_SORT($result, $(item) HASH_GET($item, "Date Due"))

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Username in Role],
    EACH [Person in Role]
  )
); 

RETURN 
ARRAY_SELECT(
  $data, $(i, item) 
  LET $username := ARRAY_INDEX($item, 1); 
  LET $person := ARRAY_INDEX($item, 2); 
  RETURN COALESCE($username, $person)
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Role is not a System User],
    EACH [Individual in Role]
  )
); 

RETURN 
ARRAY_SELECT(
  $data, $(i, item) 
  LET $notSystemUser := ARRAY_INDEX($item, 1); 
  LET $username := ARRAY_INDEX($item, 2);
  RETURN 
  IF(
	$notSystemUser == TRUE, 
	  $username, 

	$username <> NULL, 
	  EBMS_GET_ROLE_DISPLAYNAME($username)
  )
)

LET $columns := ARRAY( "RecordID", "Contract Title", "Contract Number", "Current End Date", "Total Contract Value" );
LET $raw := DB_LOOKUPALL( $columns, "Contract", "[LineNumber] FAST_EQUAL_TO 1", HASH("parent",ARRAY(EFormType,RecordID)) );
LET $result := ARRAY_SELECT($raw, $(i,item) INDEXES_TO_KEYS($item, $columns));
RETURN $result

TriggerCustomEvent.TargetRecordsFormula:
LET $hash := HASH("parent", ARRAY("Contract", [RecordID]));

LET $recordIDs :=
DB_LOOKUPALL(
	"RecordID", 
	"Extension", 
	"[LineNumber] = 1",
	HASH_MERGE(HASH("sort", "[Extension Number] ASC"), $hash)
);

LET $result := 
ARRAY_SELECT(
  $recordIDs, $(i, item)
  ARRAY("Extension", $item)
);

RETURN $result

FormulaCriterion.Formula:
ARRAY_LENGTH(
  	ARRAY_WHERE( 
	  EACH [Potential Vendor RecordID], $(i,item) 
	  AND( $item <> NULL, [Subcontract RecordID]#$i = NULL)
	)
)>0

ARRAY_LENGTH(
  	ARRAY_WHERE( 
	  EACH [Potential Vendor RecordID], $(i,item) 
	  AND( $item <> NULL, [Subcontract RecordID]#$i <> NULL)
	)
)>0

TriggerCustomEvent.TargetRecordsFormula:
ARRAY(
  ARRAY(
    "Settings - Vendor", 
    1
  )
)

DisplayMessage.MessageTemplate:
{
LET $data := HASH_GET([ABR Data], "Data");
LET $responseCode := HASH_GET([ABR Data], "Response Code");
LET $response := HASH_GET([ABR Data], "Response");

RETURN 
IF(
  $responseCode <> 200,
  	$response,
  "This " & $eventargs & " is " & HASH_GET([ABR Data], "ABN Status") &" under " & HASH_GET([ABR Data], "Entity Name") & "."
)
}

