FieldEntry.Formula:
LET $columns :=
ARRAY(
  	"RecordID",
  	"Contract Title",
  	"Contract Number",
  	"Current End Date",
  	"Total Contract Value"
);

LET $raw :=
DB_LOOKUPALL(
	$columns,
	"Contract",
	"[LineNumber] FAST_EQUAL_TO 1 AND [Head Agreement Number] FAST_EQUAL_TO " & DB_ENCODECONSTANT([Contract Number])
);

LET $result := ARRAY_SELECT($raw, $(i,item) INDEXES_TO_KEYS($item, $columns));

RETURN $result

LET $startDate := [Start Date];
LET $year := YEAR($startDate);
LET $month := MONTH($startDate);
RETURN DATE($year, $month)

LET $columns := ARRAY( "Extension Years", "Extension Months", "Extension Days", "Status", "Extension Value", "Extension Value Incl Tax", "Tax", "End Date After Exercised", "RecordID", "Date Approved" );
LET $filter := DB_ENCODECONSTANT([RecordID]) & " = [Contract RecordID] AND [LineNumber] FAST_EQUAL_TO 1" & " AND ([Status] = 'Draft' OR [Status] = 'Submitted' OR [Status] = 'Approved' OR [Status] = 'Exercised')";
LET $data := IF( AND([Extension Data] = NULL, [Status] <> "Setup"), DB_LOOKUPALL( $columns, "Extension", $filter, HASH("parent", ARRAY(EFormType, RecordID), "credentials", [Credential]) ) );
LET $hash := ARRAY_SELECT($data, $(i, item) INDEXES_TO_KEYS($item, $columns));
LET $result := HASH( "raw", $hash,
"recordIDs", ARRAY_SELECT($hash, $(i, item) HASH_GET($item, "RecordID")),
"Total Extension Value excl Tax", SUM(ARRAY_SELECT($hash, $(i, item) HASH_GET($item, "Extension Value"))),
"Total Extension Value incl Tax", SUM(ARRAY_SELECT($hash, $(i, item) HASH_GET($item, "Extension Value Incl Tax"))),
"Total Exercised Extension Value excl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($hash, $(i, item) HASH_GET($item, "Status") = "Exercised"), $(i, item) HASH_GET($item, "Extension Value"))),
"Total Exercised Extension Value incl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($hash, $(i, item) HASH_GET($item, "Status") = "Exercised"), $(i, item) HASH_GET($item, "Extension Value Incl Tax"))),
"Total Unexercised Extension Value excl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($hash, $(i, item) AND(HASH_GET($item, "Status") <> "Rejected", HASH_GET($item, "Status") <> "Cancelled", HASH_GET($item, "Status") <> "Exercised")), $(i, item) HASH_GET($item, "Extension Value"))),
"Total Unexercised Extension Value incl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($hash, $(i, item) AND(HASH_GET($item, "Status") <> "Rejected", HASH_GET($item, "Status") <> "Cancelled", HASH_GET($item, "Status") <> "Exercised")), $(i, item) HASH_GET($item, "Extension Value Incl Tax"))),
"Current End Date", COALESCE(GREATEST(ARRAY_SELECT(ARRAY_WHERE($hash, $(i, item) HASH_GET($item, "Status") = "Exercised"), $(i, item) HASH_GET($item, "End Date After Exercised"))), [Current End Date]),
"Maximum End Date", GREATEST(ARRAY_SELECT(ARRAY_WHERE($hash, $(i, item) AND(HASH_GET($item, "Status") <> "Rejected", HASH_GET($item, "Status") <> "Cancelled")), $(i, item) HASH_GET($item, "End Date After Exercised"))),
"Notes Data", ARRAY_SELECT(ARRAY_WHERE($hash, $(i, item) HASH_GET($item, "Status") = "Exercised"), $(i, item) ARRAY(HASH_GET($item, "RecordID"), HASH_GET($item, "Date Approved"))),
"Count of Exercised Extensions", ARRAY_LENGTH(ARRAY_WHERE($hash, $(i, item) HASH_GET($item, "Status") = "Exercised")),
"Count of Submitted Extensions", ARRAY_LENGTH(ARRAY_WHERE($hash, $(i, item) HASH_GET($item, "Status") = "Submitted")),
"Count of All Extensions", ARRAY_LENGTH($hash) );
//----start summary sentences LET $sentenceData := ARRAY_SELECT( $hash, $(i, extension)
LET $result := ARRAY( IF( HASH_GET($extension, "Extension Years") <> NULL(), HASH_GET($extension, "Extension Years") & " year(s)" ), IF( HASH_GET($extension, "Extension Months") <> NULL(), HASH_GET($extension, "Extension Months") & " month(s)" ), IF( HASH_GET($extension, "Extension Days") <> NULL(), HASH_GET($extension, "Extension Days") & " day(s)" ) );
RETURN HASH( "Duration", ARRAY_JOIN(ARRAY_WHERE($result, $(i, item) $item <> NULL), ", "), "Status", HASH_GET($extension, "Status") ) );
LET $exercised := ARRAY_WHERE($sentenceData, $(i, item) HASH_GET($item, "Status") = "Exercised" );
LET $notExercised := ARRAY_WHERE($sentenceData, $(i, item) AND( HASH_GET($item, "Status") <> "Exercised", HASH_GET($item, "Status") <> "Rejected", HASH_GET($item, "Status") <> "Cancelled" ) );
LET $all := ARRAY_WHERE($sentenceData, $(i, item) AND( HASH_GET($item, "Status") <> "Rejected", HASH_GET($item, "Status") <> "Cancelled" ) );
LET $summarySentences := HASH( "Exercised", ARRAY_JOIN(ARRAY_SELECT($exercised, $(i, item) HASH_GET($item, "Duration")), ","), "Not Exercised", ARRAY_JOIN(ARRAY_SELECT($notExercised, $(i, item) HASH_GET($item, "Duration")), ","), "All", ARRAY_JOIN(ARRAY_SELECT($all, $(i, item) HASH_GET($item, "Duration")), ",") ); //----end summary sentences
LET $result := HASH_MERGE( $result, HASH("Summary Sentences", $summarySentences) );
RETURN IF( [Extension Data] = NULL, $result, [Extension Data] )

LET $columns := ARRAY( "Variation Value", "Variation Value inc Tax", "Tax", "Status", "RecordID", "Date Approved" );
LET $filter := DB_ENCODECONSTANT([RecordID]) & " = [Contract RecordID] AND [LineNumber] FAST_EQUAL_TO 1 AND ([Status] = 'Draft' OR [Status] = 'Submitted' OR [Status] = 'Approved')";
LET $data := IF( AND([Variation Data] = NULL, [Status] <> "Setup"), DB_LOOKUPALL( $columns, "Variation", $filter, HASH("parent", ARRAY(EFormType, RecordID)) ) );
LET $itemsHash := ARRAY_SELECT( $data, $(i, item) INDEXES_TO_KEYS( $item, $columns ) );
LET $result := HASH( "Total Variation Value excl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) 1 = 1), $(i, item) HASH_GET($item, "Variation Value"))),
"Total Variation Value incl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) 1 = 1), $(i, item) HASH_GET($item, "Variation Value inc Tax"))),
"Total Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) 1 = 1), $(i, item) HASH_GET($item, "Tax"))),
"Count of Variations", ARRAY_LENGTH($itemsHash),
//approved "Total Approved Variation Value excl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") = "Approved"), $(i, item) HASH_GET($item, "Variation Value"))),
"Total Approved Variation Value incl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") = "Approved"), $(i, item) HASH_GET($item, "Variation Value inc Tax"))),
"Total Approved Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") = "Approved"), $(i, item) HASH_GET($item, "Tax"))),
"Count of Approved Variations", ARRAY_LENGTH(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") = "Approved")),
//unapproved "Total Unapproved Variation Value excl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") <> "Approved"), $(i, item) HASH_GET($item, "Variation Value"))),
"Total Unapproved Variation Value incl Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") <> "Approved"), $(i, item) HASH_GET($item, "Variation Value inc Tax"))),
"Total Unapproved Tax", SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") <> "Approved"), $(i, item) HASH_GET($item, "Tax"))),
"Count of Unapproved Variations", ARRAY_LENGTH(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") <> "Approved")),
"Notes Data", ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") = "Approved"), $(i, item) ARRAY( HASH_GET($item, "RecordID"), HASH_GET($item, "Date Approved"))),
"Pending Approval", ARRAY_LENGTH(ARRAY_WHERE($itemsHash, $(i, item) HASH_GET($item, "Status") = "Submitted")) );
RETURN IF( [Variation Data] = NULL, $result,
[Variation Data] )

LET $date := HASH_GET([Extension Data], "Current End Date");
LET $calculated := IF( [Original End Date]==NULL(), NULL(), $date );
RETURN IF( [Status] == "Contract Closed", [Date Contract Closed], [Status] == "Cancelled", [Date Contract Cancelled], [Status] == "Terminated", [Date Terminated], GREATEST($calculated, [Original End Date]) )

LET $startDate := [Start Date];
LET $year := YEAR($startDate);
LET $month := MONTH($startDate);
RETURN DATE($year, $month)

LET $date := HASH_GET([Extension Data], "Current End Date");
LET $calculated := IF( [Original End Date]==NULL(), NULL(), $date );
RETURN IF( [Status] == "Contract Closed", [Date Contract Closed], [Status] == "Cancelled", [Date Contract Cancelled], [Status] == "Terminated", [Date Terminated], GREATEST($calculated, [Original End Date]) )

IF( 
  AND([Contract Events Data] <> NULL, [Contract Events Data] <> ARRAY()), 
  (
	LET $startDate := NOW();
    LET $endDate := DATEADD("day", 7, $startDate);
    RETURN 
	ARRAY_LENGTH(
	ARRAY_WHERE(
		[Contract Events Data], $(i, item) 
		AND(
			ARRAY_INDEX($item, 4) >= $startDate,	
			ARRAY_INDEX($item, 4) <= $endDate
		)
	)
)))

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns:= ARRAY( "Contract Type", "Contract Title", "Contract Summary", "Department", "Contract Category", "Contract Subcategory",
//roles "Custom Role", "Role is not a System User", "Person in Role", "Role", "Username in Role"
);
LET $values:= ARRAY_SELECT( $columns, $(c, col) CURRENT_EACHVALUE($col) );
RETURN INDEXES_TO_KEYS($values, $columns)

FieldEntry.Formula:
LET $columns :=
ARRAY(
	"URL",
  	"RecordID",
	"Status",
	"Description"
);

LET $raw := 
DB_LOOKUPALL(
	ARRAY_SKIP($columns, 1),
	"Insurance",
	"[RecordID] IN (" & DB_ENCODECONSTANT([Insurance RecordIDs]) & ")"
);

LET $data :=
ARRAY_SELECT($raw, $(i,item)
  	ARRAY_CONCAT(
	  	EBMS_GET_EFORMRECORD_URL("Insurance", ARRAY_INDEX($item, 1)),
	  	$item
	)
);

RETURN 

IF(
  	ARRAY_LENGTH([Insurance RecordIDs]) > 0,
  	ARRAY_SELECT($data, $(i,item) INDEXES_TO_KEYS($item, $columns)),
  	NULL()
)

LET $navType := HASH_GET($eventargs, "NavType"); 
//Next, Previous, Direct

LET $questions := HASH_KEYS([Wizard Questions]);

LET $currentQuestionIndex := ARRAY_INDEXOF($questions, [Current Question]); 

RETURN 
IF( 
  	$navType  = "Next", 
  	COALESCE(ARRAY_INDEX($questions, $currentQuestionIndex + 1), "Finish"), 

  	$navType  = "Previous", 
	COALESCE(ARRAY_INDEX($questions, $currentQuestionIndex - 1), "Start"),

  	$navType  = "Direct", 
  	COALESCE(HASH_GET($eventargs, "QuestionName"), "Finish")
)

LET $newStatus := HASH_GET($eventargs, "WizardStatus");
RETURN IF( $newStatus <> NULL, $newStatus, [Current Question] = "Start", "Restarted",
COALESCE([Wizard Status], "In Progress") )

LET $alreadyAccessed := [Wizard - Questions Accessed];
LET $accessed := ARRAY_CONCAT($alreadyAccessed, [Current Question]); 
RETURN ARRAY_WHERE(ARRAY_DISTINCT($accessed), $(i, item) $item <> NULL)

LET $columns:= [Contract Type Settings - Target Columns];

LET $data := 
DB_LOOKUP(
  $columns, 
  "Contract Type",
  "[RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT([Contract Type RecordID])
);

RETURN INDEXES_TO_KEYS($data, $columns)

LET $allInfo := HASH_GET([Contract Type Settings], "Enabled Additional Setup Information");
LET $questionnaireEnabled := HASH_GET([Contract Module Settings], "Supplementary Data Questionnaire Enabled") = TRUE;

LET $filtered :=
ARRAY_WHERE($allInfo, $(i,item)
	IF($item = "Questionnaire Data", $questionnaireEnabled, TRUE)
);

RETURN ARRAY_WHERE($filtered, $(i,item) $item <> NULL)

LET $approvalRequired :=
AND(
  ARRAY_INDEXOF(HASH_GET([Contract Module Settings], "Profile Supported Functions"), "Approvals") > 0, 
  HASH_GET([Contract Module Settings], "Contract Approval Required") = TRUE
);

LET $contractTypeSetupSteps := HASH_GET([Contract Type Settings], "Enabled Functions"); 

LET $additionalInfo := [Wizard - Additional Information];

LET $set := 
ARRAY_CONCAT(
  $contractTypeSetupSteps,
  IF(ARRAY_LENGTH($additionalInfo) > 0, "Additional Information"),
  IF($approvalRequired = TRUE, "Approval"),
  "Finish"
);

RETURN

ARRAY_WHERE(
	$set, $(i, item) 
	$item <> NULL
)

LET $errorID := $eventargs;

LET $currentQuestion := [Current Question]; 
LET $currentQuestionValidation := HASH_GET([Wizard - Process Properties], $currentQuestion);

LET $hits := ARRAY_WHERE(HASH_GET($currentQuestionValidation, "Validation"), $(i,item) HASH_GET($item, "Error ID") <> $errorID); 
LET $errorIDs := ARRAY_SELECTMANY($hits, $(i, item) HASH_GET($item, "Error ID"));
LET $messages := ARRAY_SELECTMANY($hits, $(i, item) HASH_GET($item, "Message"));

RETURN 
HASH(
	"errorIDs", $errorIDs, 
	"messages", $messages,
	"stepFailed", IF(ARRAY_LENGTH($errorIDs) > 0, $currentQuestion),
	"stepPassed", IF(ARRAY_LENGTH($errorIDs) <= 0, $currentQuestion)
)

LET $values :=
//get list of all items which should b e completed prior to seeking approval
ARRAY_WHERE(
	[Wizard Questions], $(i,item)
	AND(
		IF([Current Question] = "Approval", $item <> "Approval"),
		$item <> "Finish"
	)
);

//get list of all 
LET $processData :=
ARRAY_WHERE($values, $(i,item)
	ARRAY_INDEXOF(
		ARRAY_WHERE(HASH_KEYS([Wizard - Process Properties]), $(i,item)
			HASH_GET(HASH_GET([Wizard - Process Properties], $item), "Completion") = TRUE),
		$item
	) > 0
);

RETURN ARRAY_LENGTH($processData) <> ARRAY_LENGTH($values)
