Column.DefaultValueFormula:
LET $startOfLastMonth := DATE(YEAR(NOW()), MONTH(DATEADD("month", -1, NOW())));
LET $startOfThisMonth := DATE(YEAR(NOW()), MONTH(NOW()));
LET $startOfNextMonth :=DATE(YEAR(NOW()), MONTH(DATEADD("month", 1, NOW())));

LET $lastMonth := DATEFORMAT(DATEADD("month", -1, NOW()), "MMM yyyy");
LET $thisMonth := DATEFORMAT(NOW(), "MMM yyyy");
LET $now := DATEFORMAT(NOW(), "dddd, dd MMMM yyyy hh:mm tt");

RETURN 
HASH(
	"Last Month", $lastMonth,
	"This Month", $thisMonth,
	"Now", $now,
	"Start of This Month", $startOfThisMonth,
	"Start of Last Month", $startOfLastMonth,
	"Start of Next Month", $startOfNextMonth 
)

Column.Formula:
LET $multiline := DB_LOOKUPALL( ARRAY( "Likelihood", "Consequence", "Risk Rating", "Risk Score" ), "Risk Mappings", "[RecordID] = 1" );
LET $lineOne := DB_LOOKUPALL( ARRAY( "Risk Ratings", "Risk Colours", "Risk Text Colours" ), "Risk Mappings", "[RecordID] = 1 AND [LineNumber] FAST_EQUAL_TO 1" );
RETURN ARRAY_CONCAT( ARRAY($multiline), $lineOne )

Column.DefaultValueFormula:
LET $columns := ARRAY( "Status", "Start Date", "Value", "Contract Type", "Vendor", "Department" );
RETURN
ARRAY_TO_HASH( $columns, $(k,key) $key, $(v,value) NULL() )

LET $columns := ARRAY( "Keywords", "Title", "Contract Number", "Contract Type", "Department", "Start Date", "Current End Date" );
RETURN
ARRAY_TO_HASH( $columns, $(k,key) $key, $(v,value) NULL() )

