LET $descriptions := 
ARRAY(
  "Last month",
  "This month",
  "Next 3 months", 
  "Next 6 months", 
  "Next 12 months"
);

LET $countKeys := 
ARRAY(
  "7",  //# expired last month
  "9",  //# expired this month
  "20", //# expired within 3 months
  "22", //# expired within 6 months
  "24", //# expired within 12 months
);

LET $valueKeys := 
ARRAY(
  "8",  //$ expired last month
  "10", //$ expired this month
  "21", //$ expired within 3 months
  "23", //$ expired within 6 months
  "25"  //$ expired within 12 months
);

LET $data :=  ARRAY_SELECT($keys, $(i, item) HASH_GET($data, $item));

LET $rows := 
ARRAY_SELECT(
  RANGE(1, 5), $(i, item) 
  (
	LET $valueKey := ARRAY_INDEX($valueKeys, $item); 
	LET $countKey := ARRAY_INDEX($countKeys, $item); 

	LET $valueData := HASH_GET([Aggregate Results], $valueKey);
	LET $countData := HASH_GET([Aggregate Results], $countKey);

	LET $url := HASH_GET($valueData, "URL"); 
	LET $description := ARRAY_INDEX($descriptions, $item); 
	LET $count := HASH_GET($countData, "Aggregate"); 
	LET $value := HASH_GET($valueData, "Aggregate"); 

	RETURN 
	ARRAY(
	  HASH("value", $description, "url", $url),
	  HASH("value", $count, "url", $url),
	  HASH("value", $value, "url", $url)
	)
  )
);

