LET $dashboardSettings := 
HASH(
  "Dashboard Preference Name", "Contract Dashboard", 
  "Settings EForm", "Settings - Contract", 
  "Module Name", "Contracts"
); 

//------------------------ No changes below this line

LET $moduleSettings := 
(
  LET $columns := 
  ARRAY(
    "Primary Entity Name", 
    "Module Name",
	"Add New Visible in Report Tiles",

    //Custom link data 
    "Custom Link Text", 
    "Custom Link URL", 
    "Custom Link Visibility",

    //Report links
    "Report Name", 
    "Report EFormType", 
    "Report Profile Name", 
    "Report Type",
    "Report Enabled",
    "Report Visibility", 

    //Action Data
	"Action Name", 
	"Can Use Action",
	"Action Navigation",
	"Dashboard Action Type",
	"Dashboard Action EFormType",
	"Dashboard Action RecordID",
	"Dashboard Action Open Mode",

	//Vendor Action Enabled
	"Vendor Action",
	"Vendor Action Enabled", 

    "Profile Supported Functions"
  );

  LET $data := 
  DB_LOOKUPALL(
  	$columns, 
  	HASH_GET($dashboardSettings, "Settings EForm"), 
  );

  LET $hashed := 
  INDEXES_TO_KEYS(
    ARRAY_TRANSPOSE($data), 
    $columns
  );

  RETURN $hashed
);

LET $globalSettings := 
(
  LET $columns := 
  ARRAY(
    "Module Name", 
    "Module Settings EForm",
    "Module Home Page", 
    "Module Dashboard Name",
    "Module Access"
  ); 

  LET $modules := 
  DB_LOOKUPALL(
    $columns, 
    "Global Settings", 
    "[Show on Main Entry] = TRUE AND [RecordID] = 1" & 
	" AND [Module Name] IS NOT EMPTY AND [Module Type] = 'Module'" & 
	" AND [Module Settings EForm] <> " & DB_ENCODECONSTANT(HASH_GET($dashboardSettings, "Settings EForm"))
  ); 

  LET $data :=
  ARRAY_SELECT(
  	$modules, $(m, module)
    INDEXES_TO_KEYS(
	  $module, 
	  $columns
	)
  );

  LET $result := 
  ARRAY_WHERE(
  	$data, $(i, item) 
  	NOT(ISBLANK(HASH_GET($item, "Module Name") <> NULL))
  );

  RETURN $result
); 

LET $currentUser := EBMS_GET_CURRENT_USER();

RETURN 
HASH_MERGE(
  $dashboardSettings, 
  HASH("Module Settings", $moduleSettings),
  HASH("Global Settings", $globalSettings), 
  HASH("Current User", $currentUser)
)

LET $startOfThisMonth := DATE(YEAR(NOW()), MONTH(NOW())); LET $startOfLastMonth := DATEADD("month", -1, $startOfThisMonth); LET $startOfNextMonth := DATEADD("month", 1, $startOfThisMonth); LET $threeMonthsFromNow := DATEADD("month", 3, $startOfThisMonth); LET $sixMonthsFromNow := DATEADD("month", 6, $startOfThisMonth); LET $twelveMonthsFromNow := DATEADD("month", 12, $startOfThisMonth);
LET $contractProfile := "Contracts"; LET $extensionProfile := "Extensions"; LET $variationProfile := "Variations";
RETURN ARRAY( HASH( "Report ID", "1", "Description", "Active Contracts", "Format", "number", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Status", "IN", ARRAY("Active", "Current")) ) ), HASH( "Report ID", "2", "Description", "Value of Active Contracts", "Format", "dollar", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "SUM([Total Contract Value])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Status", "IN", ARRAY("Active", "Current")) ) ), HASH( "Report ID", "7", "Description", "Contracts Expired Last Month", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfLastMonth, " AND "), ARRAY("Current End Date", "<", $startOfThisMonth) ) ), HASH( "Report ID", "8", "Description", "Value of Contracts Expired Last Month", "Format", "dollar", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "SUM([Total Contract Value])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfLastMonth, " AND "), ARRAY("Current End Date", "<", $startOfThisMonth) ) ), HASH( "Report ID", "9", "Description", "Contracts Expired This Month", "Format", "number", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfThisMonth, " AND "), ARRAY("Current End Date", "<", $startOfNextMonth) ) ), HASH( "Report ID", "10", "Description", "Value of Contracts Expired This Month", "Format", "dollar", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "SUM([Total Contract Value])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfThisMonth, " AND "), ARRAY("Current End Date", "<", $startOfNextMonth) ) ), HASH( "Report ID",