iption", "Value of Variations Approved Last Month", "Format", "dollar", "Form", "Variation", "Profile", $variationProfile, "Aggregate", "SUM([Variation Value])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Status", "=", "Approved", " AND "), ARRAY("Date Approved", ">=", $startOfLastMonth, " AND "), ARRAY("Date Approved", "<", $startOfThisMonth) ) ), HASH( "Report ID", "18", "Description", "Variations Approved This Month", "Format", "number", "Form", "Variation", "Profile", $variationProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Status", "=", "Approved", " AND "), ARRAY("Date Approved", ">=", $startOfThisMonth, " AND "), ARRAY("Date Approved", "<", $startOfNextMonth) ) ), HASH( "Report ID", "19", "Description", "Value of Variations Approved This Month", "Format", "dollar", "Form", "Variation", "Profile", $variationProfile, "Aggregate", "SUM([Variation Value])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Status", "=", "Approved", " AND "), ARRAY("Date Approved", ">=", $startOfThisMonth, " AND "), ARRAY("Date Approved", "<", $startOfNextMonth) ) ), HASH( "Report ID", "20", "Description", "Contracts Expired Next 3 Months", "Format", "number", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfThisMonth, " AND "), ARRAY("Current End Date", "<", $threeMonthsFromNow) ) ), HASH( "Report ID", "21", "Description", "Value of Contracts Expired Next 3 Months", "Format", "dollar", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "SUM([Total Contract Value])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfThisMonth, " AND "), ARRAY("Current End Date", "<", $threeMonthsFromNow) ) ), HASH( "Report ID", "22", "Description", "Contracts Expired Next 3 Months", "Format", "number", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfThisMonth, " AND "), ARRAY("Current End Date", "<", $sixMonthsFromNow) ) ), HASH( "Report ID", "23", "Description", "Value of Contracts Expired Next 3 Months", "Format", "dollar", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "SUM([Total Contract Value])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfThisMonth, " AND "), ARRAY("Current End Date", "<", $sixMonthsFromNow) ) ), HASH( "Report ID", "24", "Description", "Contracts Expired Next 12 Months", "Format", "number", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "COUNT([RecordID])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfThisMonth, " AND "), ARRAY("Current End Date", "<", $twelveMonthsFromNow) ) ), HASH( "Report ID", "25", "Description", "Value of Contracts Expired Next 12 Months", "Format", "dollar", "Form", "Contract", "Profile", $contractProfile, "Aggregate", "SUM([Total Contract Value])", "Filters", ARRAY( ARRAY("LineNumber", "=", 1, " AND "), ARRAY("Current End Date", ">=", $startOfThisMonth, " AND "), ARRAY("Current End Date", "<", $twelveMonthsFromNow) ) ) )

LET $run := TRUE;//[Aggregate Results] = NULL; 

//CUSTOM REPORTS //
LET $customReportSets:= 
HASH(
	"Most Assigned Status", 	HASH("Type", "Status", 		"Form", "Contract",	"Target Field", "[Status]", 			"Profile", "Contracts",		"Filter", "[LineNumber] = 1"),
	"Most Assigned Role", 		HASH("Type", "Role", 		"Form", "Contract",	"Target Field", "[" & [Role Filter] & "]", "Profile", "Contracts",	"Filter", "[LineNumber] = 1")
);

LET $valueField := "Total Contract Value";

LET $customReports := 
ARRAY_SELECT(
	HASH_KEYS($customReportSets), $(i, item) 
	(

		LET $targetField := HASH_GET(HASH_GET($customReportSets, $item), "Target Field");
		LET $filter := HASH_GET(HASH_GET($customReportSets, $item), "Filter");
		LET $type := HASH_GET(HASH_GET($customReportSets, $item), "Type");
		LET $profile  := HASH_GET(HASH_GET($customReportSets, $item), "Profile");
		LET $form := HASH_GET(HASH_GET($customReportSets, $item), "Form");

		LET $data := 
		IF( 
		  $run = TRUE, 
		  DB_REPORT(
			ARRAY(
				$targetField, 
				"COUNT([RecordID])", 
				"SUM([" & $valueField & "])"
			), 
			$form, 
			"[LineNumber] FAST_EQUAL_TO 1 AND " & $filter
		));

		LET $sorted :=
		ARRAY_SORTDESC(
			$data, $(item) 
			ARRAY_INDEX($item, 2)
		);

		LET $individualHash := 
		ARRAY_SELECT(
			$sorted, $(i, item) 

			LET $formatted := 
			ARRAY(
				ARRAY_INDEX($item, 1),
				NUMBERFORMAT(ARRAY_INDEX($item, 2), "#,##0"),
				"$" & NUMBERFORMAT(ARRAY_INDEX($item, 3), "#,##0"), 
				"TabularReport.aspx?EFormType=" & URLENCODE($form) & "&profile="& URLENCODE($profile) & "&where=" & URLENCODE($filter) & URLENCODE(" AND " & $targetField & " = '" & ARRAY_INDEX($item, 1) & "'")
			); 

			RETURN
			INDEXES_TO_KEYS($formatted, ARRAY($type, "Aggregate", "Value", "URL"))
		);

		RETURN 
		INDEXES_TO_KEYS(
			$individualHash,
			ARRAY_SELECT(RANGE(1, ARRAY_LENGTH($sorted)), $(i, item) $type & "-" & $item)
		)
	)
);

LET $customReports := 
INDEXES_TO_KEYS(
	$customReports, 
	HASH_KEYS($customReportSets)
);
//END CUSTOM REPORTS //


LET $getURL := $(form, aggregate, filters, profile)(
	LET $urlFilterString :=  
	ARRAY_JOIN(
		ARRAY_SELECT(
			$filters, $(i, item)
			IF(	
				EBMS_CONTAINS(ARRAY_INDEX($item, 1), "STEP TO"), 
				DB_ENCODEIDENTIFIER("RecordID") & " " & ">=" & " " & 1, 

				DB_ENCODEIDENTIFIER(ARRAY_INDEX($item, 1))
				& " " & ARRAY_INDEX($item, 2)
				& " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3))
			)
			& IF(ARRAY_INDEX($item, 4) <> NULL(), " " & ARRAY_INDEX($item, 4))

		), 
		"" 
	);

	RETURN 
	"TabularReport.aspx?" & 
	"EFormType=" 		& URLENCODE($form) & 
	"&profile="		& URLENCODE($profile) & 
	"&where=" 		& URLENCODE($urlFilterString)
);

LET $getAggregate := $(form, aggregate, filters, format)(
	LET $reportFilterString := 
	ARRAY_JOIN(
		ARRAY_SELECT(
			$filters, $(i, item) 
			IF(	
				EBMS_CONTAINS(ARRAY_INDEX($item, 1), "STEP TO"), 
				ARRAY_INDEX($item, 1) & ARRAY_INDEX($item, 2) & " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3)),
				"[" & ARRAY_INDEX($item, 1) & "] " & ARRAY_INDEX($item, 2) & " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3))
			)
		),
		" AND "
	);

	LET $data := 
	ARRAY_FIRST(ARRAY_FIRST(
		IF( 
		  $run = TRUE, 
		  DB_REPORT(
			$aggregate, 
			$form, 
			$reportFilterString
		)
	)));

	RETURN 
	IF( 
		$format = "dollar", 
			"$" & NUMBERFORMAT($data, "#,##0"),

		$format = "number", 
			NUMBERFORMAT($data, "#,##0"),

		$format = "none", 
			$data, 

		$data
	)
);


LET $results := 
ARRAY_SELECT(
	[Aggregate Requests], $(i, item) 
	ARRAY(
		HASH_GET($item, "Report ID"), 
			HASH(
			"Description", 
			HASH_GET($item, "Description"), 

			"User", 
			HASH_GET($item, "User"), 

			"Status", 
			HASH_GET($item, "Status"), 

			"Aggregate", 
			CALL(
				$getAggregate, 
				HASH_GET($item, "Form"), 
				HASH_GET($item, "Aggregate"), 


			  (
				LET $filter := HASH_GET($item, "Filters");
				LET $lastFilter := ARRAY(ARRAY_CONCAT(ARRAY_LAST($filter), ARRAY(" AND ")));
				LET $filter := ARRAY_CONCAT(ARRAY_TAKE($filter, ARRAY_LENGTH($filter) - 1), $lastFilter); 

				RETURN 
				ARRAY_CONCAT(
				  $filter
				)
			  ),

				HASH_GET($item, "Format")
			),

			"URL", 
			CALL(
				$getURL, 
				HASH_GET($item, "Form"), 
				HASH_GET($item, "Aggregate"), 
				HASH_GET($item, "Filters"),	
				HASH_GET($item, "Profile")
			)	
		)
	)
);

RETURN 
HASH_MERGE(
	ARRAY_TO_HASH(
		$results, 
		$(i, item) ARRAY_INDEX($item, 1),
		$(i, item) ARRAY_INDEX($item, 2)
	), 
	$customReports
)

LET $preference := 
EBMS_GET_PREFERENCE(
  HASH_GET(
	[Dashboard Settings], 
    "Dashboard Preference Name"
  )
);

RETURN 
IF(
  ARRAY_INDEXOF([Profile Supported Functions], "Dashboard - Customise Report Tiles") <= 0, 
  ARRAY("All Records"), 

  //Here you can set default preferences for people in different roles
  //user in role x 
  //preference for user in role x

  $preference
)

LET $currentUser := HASH_GET([Dashboard Settings], "Current User"); 

LET $currentRoles := EBMS_GET_ANCESTOR_ROLES($currentUser);

RETURN 
INDEXES_TO_KEYS(
  ARRAY_SELECT($currentRoles, $(i, item) TRUE),
  $currentRoles
)

LET $modules:= HASH_GET([Dashboard Settings], "Global Settings");

LET $data :=
ARRAY_SELECT(
	$modules, $(m, module)

	LET $moduleName 	:= 	HASH_GET($module, "Module Name"); 
	LET $moduleSettings :=	HASH_GET($module, "Module Settings EForm"); 
	LET $moduleEForm 	:=	HASH_GET($module, "Module Home Page"); 
	LET $displayText 	:=	HASH_GET($module, "Module Name"); //"Module Dashboard Name"); 

	LET $accessList := HASH_GET($module, "Module Access");


	LET $hasAccess :=
	ARRAY_LENGTH(
		ARRAY_WHERE(
			$accessList, $(i, item) 
			ARRAY_LENGTH(
				ARRAY_WHERE(
					$item, $(i, item) 
					HASH_GET([User to Group Mappings], $item)= TRUE
				)
			) > 0
		)
	) > 0;

	RETURN 
	IF( 
		$hasAccess == TRUE, 
		IF( 
			$moduleEForm == [EFormType], 
			$displayText,
			HASH(
				"text", 	$displayText, 
				"value", 	$moduleEForm
			)
		)
	)
);

LET $result := 
ARRAY_WHERE(
	$data, $(i, item) 
	$item<> NULL
);
RETURN $result

LET $showPerLine := 3;

LET $keys := 
ARRAY(
  "Custom Link Text", 
  "Custom Link URL", 
  "Custom Link Visibility"
); 

LET $data :=
ARRAY_TRANSPOSE(
  ARRAY_SELECT(
    $keys, $(k, key) 
    HASH_GET(HASH_GET([Dashboard Settings], "Module Settings"), $key)
  )
); 

LET $filtered := 
ARRAY_WHERE(
  $data, $(i, item)
  AND(
    ARRAY_INDEX($item, ARRAY_INDEXOF($keys, "Custom Link Text")) <> NULL
  )
); 

LET $filtered := 
ARRAY_WHERE(
	$filtered, $(i, item) 
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_INDEX($item, 3), $(i, item)
			HASH_GET([User to Group Mappings], T($item))
		)
	) > 0
);

LET $set := 
ARRAY_SELECT(
	$filtered, $(i, item) 
	ARRAY_TAKE($item, 2)
);

LET $lines := CEILING(ARRAY_LENGTH($set) / $showPerLine );

LET $result :=  
ARRAY_SELECT(
	RANGE(0, $lines), $(l, line)
	ARRAY_TAKE(ARRAY_SKIP($set, $showPerLine * ($l - 1)), $showPerLine)
);

RETURN 
$result

LET $data := HASH_GET([Dashboard Settings], "Module Settings");  
LET $currentUser := HASH_GET([Dashboard Settings], "Current User"); 

LET $columns := 
ARRAY(
  "Action Name", 
  "Can Use Action",
  "Action Navigation",
  "Dashboard Action Type",
  "Dashboard Action EFormType",
  "Dashboard Action RecordID",
  "Dashboard Action Open Mode"
);

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY_SELECT(
    $columns, $(i, column) 
    HASH_GET($data, $column)
  )
); 

LET $data := 
ARRAY_WHERE(
  $data, $(i, item) 
  ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Action Name")) <> NULL
);

LET $filtered := 
ARRAY_WHERE(
	$data, $(i, item) 
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_INDEX($item, 2), $(i, item) 
			HASH_GET([User to Group Mappings], T($item))
		)
	) > 0
);

LET $clean := 
ARRAY_SELECT(
	$filtered, $(i,item) 
	HASH(
		"Action", 			ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Action Name")),
		"URL", 				ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Action Navigation")),
		"Navigation Type", 	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Dashboard Action Type")),
		"EForm Target", 	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Dashboard Action EFormType")),
		"RecordID",			ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Dashboard Action RecordID")),
		"Open Mode", 		ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Dashboard Action Open Mode"))
	)
);

RETURN $clean

LET $data := HASH_GET([Dashboard Settings], "Module Settings");  

LET $columns := 
ARRAY(
  "Report Name", 
  "Report EFormType", 
  "Report Profile Name", 
  "Report Type",
  "Report Enabled",
  "Report Visibility" //this must be last in the set
);

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY_SELECT(
    $columns, $(i, column) 
    HASH_GET($data, $column)
  )
); 


LET $data := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Enabled")) = TRUE, 
	OR(
      ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Type")) = "Link and Export", 
	  ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Type")) = "Chart",
	  ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Type")) = "EForm"
	)
  )
);

LET $filtered := 
ARRAY_WHERE(
	$data, $(i, item) 
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Report Visibility")), $(i, item) 
			HASH_GET([User to Group Mappings], T($item))
		)
	) > 0
);

LET $result := 
ARRAY_SELECT(
  $filtered, $(i, item) 
  INDEXES_TO_KEYS(
    $item,
	$columns
  )
);

LET $grouped :=  
HASH(
  "Link and Export", ARRAY_WHERE($result, $(i, item) HASH_GET($item, "Report Type") = "Link and Export"),
  "Visual Report", ARRAY_WHERE($result, $(i, item) HASH_GET($item, "Report Type") = "Chart"),
  "EForm Report" , ARRAY_WHERE($result, $(i, item) HASH_GET($item, "Report Type") = "EForm")
);

RETURN $grouped

LET $numberPerRow := 1; 

