  RANGE(0, $loops), $(i, item)
  ARRAY_TAKE(ARRAY_SKIP($data, ($i - 1)), $numberPerRow)
);

RETURN $result

LET $numberPerRow := 1; 

//----

LET $data := HASH_GET([Report Data], "EForm Report"); 
LET $loops := CEILING(ARRAY_LENGTH($data) / $numberPerRow);

LET $result :=  
