  RANGE(0, $loops), $(i, item)
  ARRAY_TAKE(ARRAY_SKIP($data, ($i - 1)), $numberPerRow)
);

RETURN $result

LET $multiline := DB_LOOKUPALL( ARRAY( "Likelihood", "Consequence", "Risk Rating", "Risk Score" ), "Risk Mappings", "[RecordID] = 1" );
LET $lineOne := DB_LOOKUPALL( ARRAY( "Risk Ratings", "Risk Colours", "Risk Text Colours" ), "Risk Mappings", "[RecordID] = 1 AND [LineNumber] FAST_EQUAL_TO 1" );
RETURN ARRAY_CONCAT( ARRAY($multiline), $lineOne )

LET $data := HASH_GET([Dashboard Settings], "Module Settings");  

LET $columns := 
ARRAY(
  "Vendor Action", 
  "Vendor Action Enabled"
);

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY_SELECT(
    $columns, $(i, column) 
    HASH_GET($data, $column)
  )
); 

