SetUserPreference.PreferenceValueFormula:
LET $target := $eventargs; //string of a single report name OR an array of report names
LET $preference := EBMS_GET_PREFERENCE(HASH_GET([Dashboard Settings], "Dashboard Preference Name")); 

LET $action := 
IF( 
	ARRAY_INDEXOF($preference, $target) > 0, 
	"Remove", 
	"Add"
);

LET $set := 
IF( 
	$action = "Add", 
	ARRAY_CONCAT(
		$preference, 
		ARRAY($target)
	),
	ARRAY_WHERE($preference, $(i ,item) $item <> $target)
);

RETURN 
IF( 
  AND(
	$preference = NULL, 
	ARRAY_LENGTH($target) > 0
  ), 
  ARRAY_DISTINCT(ARRAY_CONCAT([Current User Dashboard Preference], $target)),
  ARRAY_DISTINCT(ARRAY_WHERE($set, $(i,item) $item <> NULL()))
)

LET $target := $eventargs; //string of a single report name OR an array of report names
LET $preference := EBMS_GET_PREFERENCE(HASH_GET([Dashboard Settings], "Dashboard Preference Name")); 

LET $action := 
IF( 
	ARRAY_INDEXOF($preference, $target) > 0, 
	"Add", 
	"Add"
);

LET $set := 
IF( 
	$action = "Add", 
	ARRAY_CONCAT(
		$preference, 
		ARRAY($target)
	),
	ARRAY_WHERE($preference, $(i ,item) $item <> $target)
);

RETURN 
IF( 
  AND(
	$preference = NULL, 
	ARRAY_LENGTH($target) > 0
  ), 
  ARRAY_DISTINCT(ARRAY_CONCAT([Current User Dashboard Preference], $target)),
  ARRAY_DISTINCT(ARRAY_WHERE($set, $(i,item) $item <> NULL()))
)
