COALESCE(
  [Module],
  ARRAY_INDEX(
  ARRAY_FIRST(ARRAY_WHERE(
    [Module Dashboard Data], $(i, item) 
    ARRAY_INDEX($item, 3) = [EFormType]
  )), 
  4
