//Setup
LET $data := ARRAY_TRANSPOSE([Gantt Data]);
LET $titles := ARRAY_INDEX($data, 1); 
LET $startDates := ARRAY_INDEX($data, 2); 
LET $endDates := ARRAY_INDEX($data, 3);
LET $recordIDs := ARRAY_INDEX($data, 4);  
LET $taskStatus := ARRAY_INDEX($data, 5);
LET $taskAssignee := ARRAY_INDEX($data, 6); 

LET $durationInDays := ARRAY_SELECT($endDates, $(i, item) $item - COALESCE(ARRAY_INDEX($startDates, $i), $item));

LET $earliestDate := ARRAY_FIRST($startDates);
LET $startingDayofMonth := DAY($earliestDate);

LET $initialstartingMonth := MONTH($earliestDate) - 1;
LET $startingMonth := IF($initialstartingMonth = 0, 12, $initialstartingMonth);

LET $initialstartingYear := YEAR($earliestDate); 
LET $startingYear := IF($initialstartingMonth = 0, $initialstartingYear - 1, $initialstartingYear); 

LET $earliestDate := DATE($startingYear, $startingMonth, $startingDayofMonth);

LET $latestDate := ARRAY_FIRST(ARRAY_SORTDESC($endDates, $(item) $item));
LET $endingYear := YEAR($latestDate); 
LET $endingMonth := MONTH($latestDate);

//View properties
LET $scale := [Scale]; //($latestDate - $earliestDate) ^ -1 / 0.33 * 100; //[Scale]; //
LET $diff := DATEDIFF_PARTS($latestDate, $earliestDate); 
LET $monthOfFinalYear := ARRAY_INDEX($diff, 2) + 1;
LET $months := ARRAY_INDEX($diff, 1) * 12 + ARRAY_INDEX($diff, 2) + 2;
LET $years := ARRAY_INDEX($diff, 1) + 1;
LET $drawDate := DATE($startingYear, $startingMonth); 
LET $currentDayofYearLocation := FLOOR(ABS($drawDate - TODAY())) * $scale ;


LET $leftSideBuffer := 500; //MAX(ARRAY_SELECT($titles, $(i, item) LEN($item))) * 7; 
LET $leftSideBufferSpace := $leftSideBuffer + 15 - 1; /*padding-left*/
LET $titleHeight := 35;

