LET $currentDayHTML := 
"<div style=""
    height: " & $titleHeight * ARRAY_LENGTH($titles) & "px;
    background: red;
    width: 1px;
    margin-left: " & $currentDayofYearLocation & "px;
    position: absolute;
""></div>";

LET $titleHTML := 
ARRAY_SELECT(
	$titles, $(i, item) 
	LET $textLimit := 50;
	LET $titleLen := LEN($item);
	LET $text := IF($titleLen > $textLimit, LEFT($item, $textLimit) & "...", $item);

	RETURN
	"<div class=""left-label-set"">
		<div class=""left-label-1"">" & HTMLENCODE($text) & "</div>" &
		"<div class=""left-label left-label-date"">" & HTMLENCODE(ARRAY_INDEX($startDates, $i)) & "</div>" & 
		"<div class=""left-label left-label-date"">" & HTMLENCODE(ARRAY_INDEX($endDates, $i)) & "</div>
	</div>"
);

LET $monthsHTML := $(startingMonth, numberOfMonths, year)
(

LET $result := 
"<div class=""month-grid"">" & 
ARRAY_JOIN(ARRAY_SELECT(
	RANGE(0, $numberOfMonths), $(i, item) 

		LET $rollOver := $startingMonth + ($i - 1) > 12;
		LET $year := IF($rollOver = TRUE, $year + 1, $year); 
		LET $monthNumber := MOD($startingMonth + ($i - 1), 12); 
		LET $date := DATE($year, IF($monthNumber = 0, 12, $monthNumber));
		LET $daysInMonth := DAY(DATEADD("day", -1, DATEADD("month", 1, $date))); 
		LET $width := $daysInMonth * $scale;

		LET $output := "<div style=""width: " & $width & "px;"" class=""month""><span class=""monthLabel"">" & DATEFORMAT($date, "MMM yy") & "</span></div>";


		RETURN $output

)) & "</div>";

RETURN $result
);

LET $rowHTML := 
ARRAY_JOIN(
	ARRAY_SELECT(
		$startDates, $(i, item) 
		LET $startDate := $item; 
		LET $yearNumber := YEAR($item) - $startingYear; 
		LET $monthNumber := MONTH($item) - $startingMonth + 1;
		LET $dayNumber := ABS($item - DATE($startingYear, $startingMonth));
		LET $margin := FLOOR($dayNumber) * $scale; //px number of day in year 
		LET $width := (CEILING(ARRAY_INDEX($durationInDays, $i)) + 1) * $scale; //px days
		LET $taskStatus := ARRAY_INDEX($taskStatus, $i);
		LET $taskAssignee := ARRAY_INDEX($taskAssignee, $i);
		LET $endDate := ARRAY_INDEX($endDates, $i);

		LET $taskLate := AND(TODAY() > $endDate, $taskStatus <> "Complete");

		/*status overrides for if not using actual status field*/
		LET $taskInProgress := AND(TODAY() <= $endDate, TODAY() >= $startDate);
		LET $taskComplete := AND(TODAY() >= $endDate, TRUE /*plug in actual rule here*/);
		LET $taskInFuture := AND(TODAY() < $startDate);

		LET $rowColour := 
		IF( 
			$taskInFuture = TRUE, 				"#2196F3",
			$taskInProgress = TRUE, 			"#f3cf21",
			$taskComplete = TRUE, 			"#5ef321",
			$taskLate = TRUE, 					"#f32121",
			$taskStatus = "Complete", 		"#5ef321",
			$taskStatus = "In Progress", 	"#f3cf21",
			$taskStatus = "Not Started", 	"#2196F3",
			"#2196F3"
		);			

		RETURN

		"<div class=""gantt-row"">" & 
				"<div class=""gantt-range"" data-clickid=""OPEN-" & ARRAY_INDEX($recordIDs, $i) &""" style=""margin-left:" & HTMLENCODE($margin) & "px;" & 
				"background: " & $rowColour & ";" & 
				"width: " & HTMLENCODE($width) & "px"">" & 
            "<span style=""z-index: 2; right:" & $width - 5 & "px;"" class=""start-label"">" & HTMLENCODE(DATEFORMAT($item, "MMM dd")) & "</span>" & 
            "<span style=""z-index: 2; left:" & $width - 5 & "px;"" class=""end-label"">" & HTMLENCODE(DATEFORMAT($endDate, "MMM dd")) & "</span>" &
          "</div>" &
        "</div>"
	)
);


LET $yearHTML := 
ARRAY_SELECT(
	RANGE(0, $years), $(i, item) 

	LET $finalYear := IF($i = $years, TRUE); 
	LET $numberofMonths := IF($finalYear, $monthOfFinalYear + 1, 12);
	LET $year := $startingYear + $i -1;
	LET $firstDayofYear := DATE(YEAR($year), $startingMonth);
	LET $endDateofPeriod := DATEADD("month", $numberofMonths, $firstDayofYear);
	LET $daysInYear := CEILING($endDateofPeriod - $firstDayofYear);
	LET $daysInPreviousYear := $firstDayofYear - DATEADD("year", -1, $firstDayofYear);
	LET $left := IF($i > 1, ($i - 1) * $daysInPreviousYear * $scale, 0);
	LET $width := (CEILING($daysInYear) + 1) * $scale;

	RETURN
	"<div style=""width: " & $width & "px;" & 
	"left: " & $left & "px;"" class=""year"">" & 
		ARRAY_JOIN(CALL($monthsHTML, $startingMonth, $numberofMonths, $year)) & \	
	"</div>"
);

LET $HTML := 
"<div class=""gantt-wrapper"">" & 
	"<div class=""gantt-scroll-wrapper"">" & 
	"<div class=""gantt-scroll"">" & 
	"<div class=""left-labels"">" & ARRAY_JOIN($titleHTML) & 
	"</div>" & 
	"<div class=""scrollArea"">" & 
		"<div class=""grid-wrapper"">" & 
		ARRAY_JOIN($yearHTML) &
		"</div>" & //end Grid Wrapper	
		"<div class=""row-wrapper"">" &
			$currentDayHTML &
			ARRAY_JOIN($rowHTML) &
		"</div>" & //end Row Wrapper
	"</div>" & //end Scroll
	"</div>" & //end Scroll
	"</div>" & //end Gantt Scroll Wrapper
"</div>"; //end Gantt Grantt Wrapper

LET $result := $HTML & $CSS;

RETURN 
$result

