LET $customReportSets:= 
HASH(
	"Most Assigned Status", 	HASH("Type", "Status", 		"Form", "Procurement",	"Target Field", "[Status]", 			"Profile", "Details",		"Filter", "[LineNumber] FAST_EQUAL_TO 1 AND [Status] <> 'Awarded' AND [Status] <> 'Awarding'"),
	"Most Assigned Role", 		HASH("Type", "Role", 		"Form", "Procurement",	"Target Field", "[" & [Role Filter] & "]", "Profile", "Details",		"Filter", "[LineNumber] FAST_EQUAL_TO 1 AND [" & [Role Filter] & "] IS NOT NULL")
);

LET $valueField := "Estimated Value";

LET $customReports := 
ARRAY_SELECT(
	HASH_KEYS($customReportSets), $(i, item) 
	(

		LET $targetField := HASH_GET(HASH_GET($customReportSets, $item), "Target Field");
		LET $filter := HASH_GET(HASH_GET($customReportSets, $item), "Filter");
		LET $type := HASH_GET(HASH_GET($customReportSets, $item), "Type");
		LET $profile  := HASH_GET(HASH_GET($customReportSets, $item), "Profile");
		LET $form := HASH_GET(HASH_GET($customReportSets, $item), "Form");

		LET $data := 
		IF( 
		  $run = TRUE, 
		  DB_REPORT(
			ARRAY(
				$targetField, 
				"COUNT([RecordID])", 
				"SUM([" & $valueField & "])"
			), 
			$form, 
			"[LineNumber] FAST_EQUAL_TO 1 AND " & $filter
		  )
		 );

		LET $sorted :=
		ARRAY_SORTDESC(
			$data, $(item) 
			ARRAY_INDEX($item, 3)
		);

		LET $individualHash := 
		ARRAY_SELECT(
			$sorted, $(i, item) 

			LET $formatted := 
			ARRAY(
				ARRAY_INDEX($item, 1),
				NUMBERFORMAT(ARRAY_INDEX($item, 2), "#,##0"),
				"$" & NUMBERFORMAT(ARRAY_INDEX($item, 3), "#,##0"), 
				"TabularReport.aspx?EFormType=" & URLENCODE($form) & "&profile="& URLENCODE($profile) & "&where=" & URLENCODE($filter) & URLENCODE(" AND " & $targetField & " = '" & ARRAY_INDEX($item, 1) & "'")
			); 

			RETURN
			INDEXES_TO_KEYS($formatted, ARRAY($type, "Aggregate", "Value", "URL"))
		);

		RETURN 
		INDEXES_TO_KEYS(
			$individualHash,
			ARRAY_SELECT(RANGE(1, ARRAY_LENGTH($sorted)), $(i, item) $type & "-" & $item)
		)
	)
);

LET $customReports := 
INDEXES_TO_KEYS(
	$customReports, 
	HASH_KEYS($customReportSets)
);
//END CUSTOM REPORTS //


LET $getURL := $(form, aggregate, filters, profile)(
	LET $urlFilterString :=  
	ARRAY_JOIN(
		ARRAY_SELECT(
			$filters, $(i, item)
			IF(	
				EBMS_CONTAINS(ARRAY_INDEX($item, 1), "STEP TO"), 
				DB_ENCODEIDENTIFIER("RecordID") & " " & ">=" & " " & 1, 

				DB_ENCODEIDENTIFIER(ARRAY_INDEX($item, 1))
				& " " & ARRAY_INDEX($item, 2)
				& " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3))
			)
			& IF(ARRAY_INDEX($item, 4) <> NULL(), " " & ARRAY_INDEX($item, 4))

		), 
		"" 
	);

	RETURN 
	"TabularReport.aspx?" & 
	"EFormType=" 		& URLENCODE($form) & 
	"&profile="		& URLENCODE($profile) & 
	"&where=" 		& URLENCODE($urlFilterString)
);

LET $getAggregate := $(form, aggregate, filters, format)(
	LET $reportFilterString := 
	ARRAY_JOIN(
		ARRAY_SELECT(
			$filters, $(i, item) 
			IF(	
				EBMS_CONTAINS(ARRAY_INDEX($item, 1), "STEP TO"), 
				ARRAY_INDEX($item, 1) & ARRAY_INDEX($item, 2) & " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3)),

				"[" & ARRAY_INDEX($item, 1) & "] " & ARRAY_INDEX($item, 2) & " " & DB_ENCODECONSTANT(ARRAY_INDEX($item, 3))& " " & ARRAY_INDEX($item, 4)
			)
		),
		""
	);

	LET $data := 
	ARRAY_FIRST(ARRAY_FIRST(
		IF($run = TRUE, DB_REPORT(
			$aggregate, 
			$form, 
			$reportFilterString
		))
	));

	RETURN 
	IF( 
		$format = "dollar", 
			"$" & NUMBERFORMAT($data, "#,##0"),

		$format = "number", 
			NUMBERFORMAT($data, "#,##0"),

		$format = "none", 
			$data, 

		$data
	)
);

LET $results := 
ARRAY_SELECT(
	[Aggregate Requests], $(i, item) 
	ARRAY(
		HASH_GET($item, "Report ID"), 
			HASH(
			"Description", 
			HASH_GET($item, "Description"), 

			"User", 
			HASH_GET($item, "User"), 

			"Status", 
			HASH_GET($item, "Status"), 

			"Aggregate", 
			CALL(
				$getAggregate, 
				HASH_GET($item, "Form"), 
				HASH_GET($item, "Aggregate"), 
				HASH_GET($item, "Filters"),
				HASH_GET($item, "Format")
			),

			"URL", 
			CALL(
				$getURL, 
				HASH_GET($item, "Form"), 
				HASH_GET($item, "Aggregate"), 
				HASH_GET($item, "Filters"),	
				HASH_GET($item, "Profile")
			)	
		)
	)
);

LET $result :=  
HASH_MERGE(
	ARRAY_TO_HASH(
		$results, 
		$(i, item) ARRAY_INDEX($item, 1),
		$(i, item) ARRAY_INDEX($item, 2)
	), 
	$customReports
);

RETURN 
IF( 
  [Aggregate Results] = NULL, 
  $result, 
  [Aggregate Results]
)

