Navigate.UrlTemplate:
{
IF( 
  HASH_GET([Selected Action Data], "Navigation Type") = "URL",
  HASH_GET([Selected Action Data], "URL"),

  HASH_GET([Selected Action Data], "Navigation Type") = "Existing Record",
  EBMS_GET_EFORMRECORD_URL(
    HASH_GET([Selected Action Data], "EForm Target"),
    HASH_GET([Selected Action Data], "RecordID")
  )
)
}

Binding.Expression:
LET $reportName := "All Records";
LET $isFavorited := ARRAY_INDEXOF([Current User Dashboard Preference], $reportName) > 0;

LET $content := 
IF(
  $isFavorited = TRUE, 
  "<span style=""margin-bottom: 10px;"">" & HASH_GET([Icons], "unfilledStar") & "</span>",
  "<span style=""margin-bottom: 10px;"">" & HASH_GET([Icons], "filledStar") & "</span>"
);

LET $textColour := 
IF( 
	$isFavorited = TRUE, 
	"#2f4f4f",

	"black"
);


RETURN "<div style='color: " & $textColour & "; cursor: pointer; padding-top: 8px; font-size: 11pt' data-clickid=""" & $reportName & """>" 
& $content 
& 
IF( 
  $isFavorited = TRUE, 
  " Dashboard report",
  " Add to dashboard"
) & "</div>"

OR(
	AND(
		[Navigation] = "Dashboard", 
		ARRAY_INDEXOF([Current User Dashboard Preference], "All Records") > 0
	), 
	AND(
		[Navigation] = "Reports",
		[Report Type] = "Premade reports"
	),
    AND(
	    [Navigation] = "Find risk"
    )
)

LET $reportName := "All Audits";
LET $isFavorited := ARRAY_INDEXOF([Current User Dashboard Preference], $reportName) > 0;

LET $content := 
IF(
  $isFavorited = TRUE, 
  "<span style=""margin-bottom: 10px;"">" & HASH_GET([Icons], "unfilledStar") & "</span>",
